import{_ as e,c as n,o as t,a3 as r}from"./chunks/framework.Dcugq_a2.js";const m=JSON.parse('{"title":"VL1: Was sind Forschungsdaten?","description":"","frontmatter":{},"headers":[],"relativePath":"lz/VL_01_Forschungsdaten_LZ.md","filePath":"lz/VL_01_Forschungsdaten_LZ.md"}'),i={name:"lz/VL_01_Forschungsdaten_LZ.md"},a=r('<h1 id="vl1-was-sind-forschungsdaten" tabindex="-1">VL1: Was sind Forschungsdaten? <a class="header-anchor" href="#vl1-was-sind-forschungsdaten" aria-label="Permalink to &quot;VL1: Was sind Forschungsdaten?&quot;">​</a></h1><section><blockquote><h3 id="ausgangslage" tabindex="-1">Ausgangslage <a class="header-anchor" href="#ausgangslage" aria-label="Permalink to &quot;Ausgangslage&quot;">​</a></h3></blockquote><ul><li><p>Die S. können keine (oder nur begrenzt) Beispiele von Forschungsdaten im Studium aufzählen.</p></li><li><p>Die S. wissen nicht, welche Rolle bspw. Datenformate spielen.</p></li></ul></section><section><blockquote><h3 id="lernziele-fur-den-unterricht-in-prasenz" tabindex="-1">Lernziele für den Unterricht in Präsenz <a class="header-anchor" href="#lernziele-fur-den-unterricht-in-prasenz" aria-label="Permalink to &quot;Lernziele für den Unterricht in Präsenz&quot;">​</a></h3></blockquote><table><thead><tr><th></th><th></th><th></th></tr></thead><tbody><tr><td>1</td><td>Die S. können Forschungsdaten, von denen sie in ihrem Studium bereits gehört haben, identifizieren <a href="#4">(2)</a>.</td><td>Die S. überlegen, ob sie in ihrem Studium bereits Forschungsdaten verwendet haben um sich darüber auszutauschen und Beispiele zu sammeln.</td></tr><tr><td>2</td><td>Die S. können verschiedene Formen von Forschungsdaten benennen <a href="#4">(1)</a>.</td><td>Hier müssen die S. ihr erlerntes Wissen anwenden, um Datentypen aufzuzählen.</td></tr><tr><td>3</td><td>Die S. können zuordnen <a href="#4">(5)</a>, wie unterschiedliche Forschungsdaten erhoben werden und in welchen Formen und Formaten sie aufweisen könnten.</td><td>Hier müssen die S. ihr erlerntes Wissen umsetzen, um Datentypen mit (angegebenen) Messungen und Formen/Formaten zu verknüpfen.</td></tr></tbody></table></section><h2 id="detaillierte-kursplanung-1" tabindex="-1">Detaillierte Kursplanung[^1] <a class="header-anchor" href="#detaillierte-kursplanung-1" aria-label="Permalink to &quot;Detaillierte Kursplanung[^1]&quot;">​</a></h2><section><blockquote><h3 id="einstiegsphase" tabindex="-1">Einstiegsphase <a class="header-anchor" href="#einstiegsphase" aria-label="Permalink to &quot;Einstiegsphase&quot;">​</a></h3></blockquote><table><thead><tr><th style="text-align:center;">Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden / Medien  (Arbeitsschritte)</th><th style="text-align:center;">Sozialform</th></tr></thead><tbody><tr><td style="text-align:center;">Ca. 5 min.</td><td>Begrüßung</td><td>L. stellt sich vor und startet den Kurs.</td><td></td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">Ca. 20 min.</td><td><a href="#3">(Ice Breaker)</a></td><td>Die S. besprechen intuitiv folgendes (ohne vorherige Einleitung):<br><br> + Was sind Forschungsdaten<br> + Welche Forschungsdaten kennen sie bereits?<br> + Mit welchen Forschungsdaten haben sie bereits im Studium gearbeitet?<br><br> Die S. können Forschungsdaten mit denen sie in ihrem Studium bereits gearbeitet haben identifizieren <a href="#4">(2)</a>.</td><td>Zuerst besprechen sich die S. in Kleingruppen (2-3) und beantworten die drei Fragen.<br><br> Daraufhin werden die Antworten von jeder (einigen) Gruppe(n) im Plenum geteilt. <br> Das bietet die Möglichkeit, für die S. sich und deren Wissen zum Thema Forschungsdaten auszutauschen.</td><td style="text-align:center;">GA / PL</td></tr><tr><td style="text-align:center;">Ca. 15 min.</td><td>Einführung in den Kurs:<br><br> + Wie ist der Kurs gestaltet, <br> + was wird von den Studierenden erwartet? <br><br> Erster Blick auf Materialien und Aufgaben</td><td>L. bietet S. Orientierung: <br><br> L. macht <a href="#1">Lernziele</a>, Lernwege und Leistungserwartungen transparent <br><br> L. erklärt die Grundidee und den Mehrwert von Flipped Classroom <br><br> L. ermöglicht Zugang zu und erklärt Umgang mit E-Learning-System.</td><td>LMS</td><td style="text-align:center;">LV</td></tr></tbody></table></section><section><blockquote><h3 id="arbeitsphase" tabindex="-1">Arbeitsphase <a class="header-anchor" href="#arbeitsphase" aria-label="Permalink to &quot;Arbeitsphase&quot;">​</a></h3></blockquote><table><thead><tr><th style="text-align:center;">Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden / Medien  (Arbeitsschritte)</th><th style="text-align:center;">Sozialform</th></tr></thead><tbody><tr><td style="text-align:center;">Ca. 10 min.</td><td>Thema: Forschung</td><td>Folgende Aspekte werden von L. eingeführt und im Plenum besprochen: <br> - Was ist Forschung (überhaupt)? <br> (weitere Fragen in der Erläuterung)</td><td>Brainstorming / Diskussion</td><td style="text-align:center;">PL</td></tr><tr><td style="text-align:center;">Ca. 15 min.</td><td>Thema: Definition Forschungsdaten.</td><td>L. bezieht sich auf die Diskussion <a href="#3">(Ice Breaker)</a> in der Einstiegsphase und bespricht folgendes: <br><br> Wie kommen Forschungsdaten zustande? <br> Mit welchen Forschungsdaten wird im Studium in welchen Fächern gearbeitet? <br><br> Die S. können verschiedene Formen von Forschungsdaten benennen <a href="#4">(1)</a>.</td><td>Lehrvortrag mit Visualisierung (bspw. Foliensatz)</td><td style="text-align:center;">LV</td></tr><tr><td style="text-align:center;">Ca. 15 min.</td><td>Erläuterung der Rolle/Relevanz von Forschungsdaten in der Forschung.</td><td>Die S. können zuordnen <a href="#4">(5)</a>, wie unterschiedliche Forschungsdaten erhoben werden und in welchen Formen und Formaten sie aufweisen könnten.</td><td>Diskussion zur Verwendung der Global-Terrorism-Database</td><td style="text-align:center;">PL</td></tr></tbody></table></section><section><blockquote><h3 id="abschluss" tabindex="-1">Abschluss <a class="header-anchor" href="#abschluss" aria-label="Permalink to &quot;Abschluss&quot;">​</a></h3></blockquote><table><thead><tr><th style="text-align:center;">Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden / Medien  (Arbeitsschritte)</th><th style="text-align:center;">Sozialform</th></tr></thead><tbody><tr><td style="text-align:center;">Ca. 5 min.</td><td>Was wird als Vorbereitung für die nächste Stunde erwartet? <br><br> (Aufgaben, Umfang, Zeit, schriftliche Dokumentation)</td><td>L. erklärt Erwartungen für nächste Stunde</td><td>LMS</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">Ca. 5 min.</td><td>Klärung von offenen Fragen und Abschluss</td><td>L. bietet S. an, offene Fragen zum Vorlesungsthema, dem Seminarverlauf und/oder anderen Aspekten zu besprechen.</td><td></td><td style="text-align:center;">PL</td></tr></tbody></table><hr><p>[^1]:<strong>Sozialformen:</strong> PL = Plenum; L = Lehrende*r; S = Studierende; LV = Lehrvortrag; EA = Einzelarbeit, GA = Gruppenarbeit.</p></section><h2 id="prasenzphase-erlauterung" tabindex="-1">Präsenzphase / Erläuterung <a class="header-anchor" href="#prasenzphase-erlauterung" aria-label="Permalink to &quot;Präsenzphase / Erläuterung&quot;">​</a></h2><section><blockquote><h3 id="einstiegsphase-1" tabindex="-1">Einstiegsphase <a class="header-anchor" href="#einstiegsphase-1" aria-label="Permalink to &quot;Einstiegsphase&quot;">​</a></h3></blockquote><h4 id="ice-breaker" tabindex="-1">Ice Breaker <a class="header-anchor" href="#ice-breaker" aria-label="Permalink to &quot;Ice Breaker&quot;">​</a></h4><p>Als Einstieg in den Kurs können die S. sich untereinander austauschen und sich somit intuitiv mit dem Thema vertraut machen. Die S. diskutieren in Kleingruppen die folgenden Fragen:</p><ul><li>Was sind Forschungsdaten?</li><li>Welche Forschungsdaten kennen sie bereits?</li><li>Mit welchen Forschungsdaten haben sie bereits im Studium gearbeitet?</li></ul><p>Daraufhin leitet L. eine offene Diskussion ein, in der jede Kleingruppe ihre Gedanken/Antworten dem Plenum vorstellt.</p><p>L. fasst die Antworten zusammen und zieht daraus Schlüsse, ob die S. Vorkenntnisse zum Thema Forschungsdaten haben und wenn Vorkenntnisse vorhanden sind, mit welchen Daten die S. im Studium bereits gearbeitet haben.</p><p><strong>Einführung in den Kurs</strong></p><p>L. erklärt die Kursgestaltung:</p><ul><li>Was soll gelehrt werden? Wofür ist der Kurs da? Was sollen S. am Ende können? (Lernziele)</li></ul><ul><li>Grundidee und Mehrwerte von Blended-Learning (Lernweg)</li></ul><ul><li><p>Erster Blick auf Materialien und Aufgaben (Lernweg)</p><ul><li>L. stellt Inhalte bzw. Lernweg vor</li><li>L. stellt Umgang mit und Zugang zu E-Learning-System sicher</li><li>Was wird von den S. erwartet? Welche Prüfungsform gibt es? Was müssen S. für ECTS tun? (Leistungserwartungen)</li></ul></li><li><p>Optional: Vorstellung von Prüfungsform „Lernportfolio“ (inkl. Blick auf eine Mustervorlage)</p></li></ul></section><section><blockquote><h3 id="arbeitsphase-1" tabindex="-1">Arbeitsphase <a class="header-anchor" href="#arbeitsphase-1" aria-label="Permalink to &quot;Arbeitsphase&quot;">​</a></h3></blockquote><h4 id="diskussion-und-brainstorming" tabindex="-1">Diskussion und Brainstorming <a class="header-anchor" href="#diskussion-und-brainstorming" aria-label="Permalink to &quot;Diskussion und Brainstorming&quot;">​</a></h4><p>L. führt das übergreifende Thema „Forschung“ ein und erläutert folgende Aspekte:</p><ul><li>Was ist Forschung (überhaupt)?</li><li>Wie läuft das ab? Was passiert in der Forschung? Und worauf sollte geachtet werden?</li></ul><p>Hier sollen die S. aktiv mitreden, damit eine offene Diskussion zu dem Thema entsteht.</p><h4 id="lehrvortrag" tabindex="-1">Lehrvortrag <a class="header-anchor" href="#lehrvortrag" aria-label="Permalink to &quot;Lehrvortrag&quot;">​</a></h4><p>L. bezieht sich auf den Ice Breaker und erläutert das Thema Forschungsdaten in einem kleinem Lehrvortrag.<br> Impuls (s. Text zur VL „Was sind Forschungsdaten?“).<br> L. kann sich auf die Leitfragen (unten) beziehen.</p><ul><li>Was sind Forschungsdaten?</li><li>Was gibt es für Unterschiede?</li><li>Wie werden Forschungsdaten erhoben?</li><li>L. gibt ggf. Beispiele aus eigener Erfahrung</li><li>In welchen Studienfächern sind welche Forschungsdaten relevant?</li></ul><h4 id="interaktion-besprechung-von-fallbeispielen" tabindex="-1">Interaktion: Besprechung von Fallbeispielen <a class="header-anchor" href="#interaktion-besprechung-von-fallbeispielen" aria-label="Permalink to &quot;Interaktion: Besprechung von Fallbeispielen&quot;">​</a></h4><p>L. zeigt Fallbeispiel GTD.</p><p>S. sammeln Ideen, welche Herausforderungen sich in den exemplarisch vorgestellten Forschungsprojekten bei der Erhebung gestellt haben, zu welchen Zwecken die erhobenen Forschungsdaten nachgenutzt werden und wie die Daten kritisch interpretiert werden können. </p><h4 id="interaktion-relevanz-von-forschungsdaten-in-der-forschung" tabindex="-1">Interaktion: Relevanz von Forschungsdaten in der Forschung <a class="header-anchor" href="#interaktion-relevanz-von-forschungsdaten-in-der-forschung" aria-label="Permalink to &quot;Interaktion: Relevanz von Forschungsdaten in der Forschung&quot;">​</a></h4><p>S. ordnen Forschungsdaten bestimmten Forschungsbereichen, Formen und Formaten zu.</p></section><section><blockquote><h3 id="abschluss-1" tabindex="-1">Abschluss <a class="header-anchor" href="#abschluss-1" aria-label="Permalink to &quot;Abschluss&quot;">​</a></h3></blockquote><p>L. gibt Ausblick auf nächstes Thema und erklärt die Erwartungen für die nächste Stunde, inklusive Blick auf die Aufgaben im LMS.</p><p>L. teilt mit, in welcher Form die Aufgabe(n) erfüllt werden sollen und welchen zeitlichen Umfang die Kursvorbereitung in etwa einnimmt.[^2]</p><p>L. fragt die S., ob noch Unklarheiten oder offene Fragen vorhanden sind.</p><hr><p>[^2]: Bei Prüfungsform Lernportfolio: Schriftliche Ausarbeitung der Leitfragen in Form von Stichpunkten</p></section><h2 id="taxonomiestufen" tabindex="-1">Taxonomiestufen <a class="header-anchor" href="#taxonomiestufen" aria-label="Permalink to &quot;Taxonomiestufen&quot;">​</a></h2><table><thead><tr><th style="text-align:center;"></th><th></th><th></th></tr></thead><tbody><tr><td style="text-align:center;">Taxonomiestufe 1</td><td>Wissen, Kenntnisse (Tatsachen, Begriffe nennen)</td><td>Informationen möglichst wortgenau erinnern wiedergeben können, Informationen sinnerhaltend umformen und in eigenen Worten wiedergeben sowie zusammenfassen können</td></tr><tr><td style="text-align:center;">Taxonomiestufe 2</td><td>Verständnis (erklären, begründen, mit anderen Worten umschreiben)</td><td></td></tr><tr><td style="text-align:center;">Taxonomiestufe 3</td><td>Anwendung (Anwendung des Wissens, z.B. bei der Lösung eines praktischen Problems)</td><td>Abstraktionen (Regeln, Methoden etc.) in konkreten Situationen anwenden können</td></tr><tr><td style="text-align:center;">Taxonomiestufe 4</td><td>Analyse (eine reale Situation in ihre Elemente zerlegen)</td><td>Ideen, Problemstellungen in ihre Elemente zerlegen und vergleichen können</td></tr><tr><td style="text-align:center;">Taxonomiestufe 5</td><td>Synthese (aus verschiedenen Erlebnissen eine neue Situation konstruieren)</td><td>Einzelne Elemente zu einer Ganzheit formen</td></tr><tr><td style="text-align:center;">Taxonomiestufe 6</td><td>Bewertung (eine Situation beurteilen, überprüfen)</td><td>Ein bewertendes Urteil abgeben können</td></tr></tbody></table><h2 id="quellen" tabindex="-1">Quellen <a class="header-anchor" href="#quellen" aria-label="Permalink to &quot;Quellen&quot;">​</a></h2><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Lernziele Formulieren leicht gemacht</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/lernziele-formulieren-leicht-gemacht.htm" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/lernziele-formulieren-leicht-gemacht.htm</a></p><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Lernziele definieren</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/lernziele-definieren.html" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/lernziele-definieren.html</a></p><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Formulierungshilfen für Lernziele</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/formulierungshilfen-fur-lernziele.html" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/formulierungshilfen-fur-lernziele.html</a></p>',17),s=[a];function d(l,h,o,u,c,g){return t(),n("div",null,s)}const f=e(i,[["render",d]]);export{m as __pageData,f as default};
