import{_ as e,c as t,o as n,a3 as i}from"./chunks/framework.8CaK9a7u.js";const f=JSON.parse('{"title":"VL2: Was ist Forschungsdatenmanagement?","description":"","frontmatter":{},"headers":[],"relativePath":"lz/VL_02_Forschungsdatenmanagement_LZ.md","filePath":"lz/VL_02_Forschungsdatenmanagement_LZ.md"}'),a={name:"lz/VL_02_Forschungsdatenmanagement_LZ.md"},r=i('<h1 id="vl2-was-ist-forschungsdatenmanagement" tabindex="-1">VL2: Was ist Forschungsdatenmanagement? <a class="header-anchor" href="#vl2-was-ist-forschungsdatenmanagement" aria-label="Permalink to &quot;VL2: Was ist Forschungsdatenmanagement?&quot;">​</a></h1><section><blockquote><h3 id="ausgangslage" tabindex="-1">Ausgangslage <a class="header-anchor" href="#ausgangslage" aria-label="Permalink to &quot;Ausgangslage&quot;">​</a></h3></blockquote><ul><li>Die S. wissen nicht genau, welche Aspekte Forschungsdatenmanagement beinhaltet und weshalb es (insbesondere in Bezug auf ihr Studium) von Bedeutung ist.</li><li>Die S. wissen nicht, worauf sie beim Umgang mit Daten in ihrem Studium (insbesondere bei ihren Forschungsprojekten) achten müssen.</li></ul></section><section><blockquote><h3 id="lernziele-fur-den-online-kurs" tabindex="-1">Lernziele für den Online-Kurs <a class="header-anchor" href="#lernziele-fur-den-online-kurs" aria-label="Permalink to &quot;Lernziele für den Online-Kurs&quot;">​</a></h3></blockquote><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">1</td><td style="text-align:left;">Die S. können „Forschungsdatenmanagement“ (in eigenen Worten) beschreiben <a href="#14">(1)</a> und die Relevanz von FDM allgemein sowie in ihrem Studium erläutern <a href="#14">(2)</a>.</td><td style="text-align:left;">Die S. müssen ihr erlerntes Wissen anwenden, um Begrifflichkeiten wiederzugeben.</td></tr><tr><td style="text-align:left;">2</td><td style="text-align:left;">Die S. können die Merkmale und Vorteile von einer guten wissenschaftlichen Praxis im Forschungsprozess benennen <a href="#14">(1)</a>.</td><td style="text-align:left;">Die S. befassen sich näher mit dem Thema „Gute wissenschaftliche Praxis“ und verstehen, welche Aspekte diesbezüglich beim wissenschaftlichen Arbeiten zu beachten sind.</td></tr></tbody></table><blockquote><h3 id="lernziele-fur-den-unterricht-in-prasenz" tabindex="-1">Lernziele für den Unterricht in Präsenz <a class="header-anchor" href="#lernziele-fur-den-unterricht-in-prasenz" aria-label="Permalink to &quot;Lernziele für den Unterricht in Präsenz&quot;">​</a></h3></blockquote><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">3</td><td style="text-align:left;">Die S. sind in der Lage, das angewandte Wissen zum Thema FDM auf relevante Aspekte in ihrem Studium zu beziehen <a href="#14">(5)</a>.</td><td style="text-align:left;">Die S. können überschneidende Aspekte des FDMs in ihrem Studium wiederfinden.</td></tr><tr><td style="text-align:left;">4</td><td style="text-align:left;">Die S. können die Relevanz und Vor- und Nachteile von FDM gegenüberstellen <a href="#14">(4)</a>.</td><td style="text-align:left;">Hier müssen die S. ihr erlerntes Wissen anwenden, um die Relevanz und Vor- und Nachteile von FDM gegenüber zu stellen.</td></tr></tbody></table></section><h2 id="detaillierte-kursplanung-1" tabindex="-1">Detaillierte Kursplanung[^1] <a class="header-anchor" href="#detaillierte-kursplanung-1" aria-label="Permalink to &quot;Detaillierte Kursplanung[^1]&quot;">​</a></h2><section><blockquote><h3 id="online-phase-vorbereitung" tabindex="-1">Online-Phase (Vorbereitung) <a class="header-anchor" href="#online-phase-vorbereitung" aria-label="Permalink to &quot;Online-Phase (Vorbereitung)&quot;">​</a></h3></blockquote><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden/ Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 20 min.</td><td>Einführung in das Thema „Was ist FDM?“ durch Infotext</td><td>Die S. können „Forschungsdatenmanagement“ (in eigenen Worten) beschreiben <a href="#14">(1)</a> und die Relevanz von FDM allgemein sowie in ihrem Studium erläutern <a href="#14">(2)</a>.</td><td>Beantworten von Leitfragen (unten) im Lernportfolio.</td><td>EA</td></tr><tr><td>Ca. 20 min.</td><td>Einführung GWP und kurze Einordnung von FDM in Open Science.</td><td>Die S. können die Merkmale und Vorteile von einer guten wissenschaftlichen Praxis im Forschungsprozess benennen <a href="#14">(1)</a>.</td><td>Lesen der „Leitlinien zur Sicherung guter wissenschaftlicher Praxis“</td><td>EA</td></tr></tbody></table><hr><p>[^1]:<strong>Legende der Sozialformen:</strong> PL = Plenum; L = Lehrende*r; S = Studierende; LV = Lehrvortrag; EA = Einzelarbeit, GA = Gruppenarbeit.</p></section><section><blockquote><h3 id="prasenzphase" tabindex="-1">Präsenzphase <a class="header-anchor" href="#prasenzphase" aria-label="Permalink to &quot;Präsenzphase&quot;">​</a></h3></blockquote><blockquote><h4 id="einstiegsphase" tabindex="-1">Einstiegsphase <a class="header-anchor" href="#einstiegsphase" aria-label="Permalink to &quot;Einstiegsphase&quot;">​</a></h4></blockquote><table><thead><tr><th style="text-align:left;">Zeit</th><th style="text-align:left;">Inhalt</th><th style="text-align:left;">Ziele</th><th style="text-align:left;">Methoden/ Medien (Arbeitsschritte)</th><th style="text-align:left;">Sozialform</th></tr></thead><tbody><tr><td style="text-align:left;">Ca. 10 min.</td><td style="text-align:left;">Organisatorisches</td><td style="text-align:left;">Klärung von offenen Fragen.</td><td style="text-align:left;">Hier können jegliche Aspekte zum Kurs besprochen werden, wie z.B. Prüfungsleistungen oder die Nutzung LiaScript.</td><td style="text-align:left;">PL</td></tr></tbody></table><blockquote><h3 id="arbeitsphase" tabindex="-1">Arbeitsphase <a class="header-anchor" href="#arbeitsphase" aria-label="Permalink to &quot;Arbeitsphase&quot;">​</a></h3></blockquote><table><thead><tr><th style="text-align:left;">Zeit</th><th style="text-align:left;">Inhalt</th><th style="text-align:left;">Ziele</th><th style="text-align:left;">Methoden/ Medien (Arbeitsschritte)</th><th style="text-align:left;">Sozialform</th></tr></thead><tbody><tr><td style="text-align:left;">Ca. 15 min.</td><td style="text-align:left;">Wissenspool zum Thema FDM</td><td style="text-align:left;">L. schafft Basis für gemeinsame Arbeit</td><td style="text-align:left;">Wissenspool (Mindmap)</td><td style="text-align:left;">PL</td></tr><tr><td style="text-align:left;">Ca. 30 min.</td><td style="text-align:left;">Offene Diskussion im Plenum zur Relevanz und den Vor- und Nachteilen vom FDM (mit Bezug auf das Studium).</td><td style="text-align:left;">Die S. können die Relevanz und Vor- und Nachteile von FDM gegenüberstellen <a href="#14">(4)</a>. <br>Die S. sind in der Lage, das angewandte Wissen zum Thema FDM auf relevante Aspekte in ihrem Studium zu beziehen <a href="#14">(5)</a>.</td><td style="text-align:left;">Diskussion</td><td style="text-align:left;">PL</td></tr><tr><td style="text-align:left;">Ca. 25 min.</td><td style="text-align:left;">Übung zum DFG Kontext <br>Diskussion im Plenum</td><td style="text-align:left;">Die S. können die Merkmale und Vorteile von einer guten wissenschaftlichen Praxis im Forschungsprozess benennen <a href="#14">(1)</a>.</td><td style="text-align:left;">Arbeit in Kleingruppen und Beantwortung von vorbereiteten Fragen <br>Anschließende Diskussion im Plenum</td><td style="text-align:left;">GA / PL</td></tr></tbody></table><blockquote><h3 id="abschluss" tabindex="-1">Abschluss <a class="header-anchor" href="#abschluss" aria-label="Permalink to &quot;Abschluss&quot;">​</a></h3></blockquote><table><thead><tr><th style="text-align:left;">Zeit</th><th style="text-align:left;">Inhalt</th><th style="text-align:left;">Ziele</th><th style="text-align:left;">Methoden/ Medien (Arbeitsschritte)</th><th style="text-align:left;">Sozialform</th></tr></thead><tbody><tr><td style="text-align:left;">Ca. 5 min.</td><td style="text-align:left;">Was wird als Vorbereitung für die nächste Stunde erwartet? (Aufgaben und Umfang Zeit, schriftliche Dokumentation)</td><td style="text-align:left;">L. erklärt Erwartungen für nächste Stunde</td><td style="text-align:left;">LMS</td><td style="text-align:left;"></td></tr><tr><td style="text-align:left;">Ca. 5 min.</td><td style="text-align:left;">Klärung von offenen Fragen und Abschluss</td><td style="text-align:left;">L. bietet S. an, offene Fragen zum Vorlesungsthema, dem Seminarverlauf und/oder anderen Aspekten zu besprechen.</td><td style="text-align:left;"></td><td style="text-align:left;">PL</td></tr></tbody></table></section><h2 id="prasenzphase-erlauterung" tabindex="-1">Präsenzphase / Erläuterung <a class="header-anchor" href="#prasenzphase-erlauterung" aria-label="Permalink to &quot;Präsenzphase / Erläuterung&quot;">​</a></h2><section><blockquote><h3 id="einstiegsphase-1" tabindex="-1">Einstiegsphase <a class="header-anchor" href="#einstiegsphase-1" aria-label="Permalink to &quot;Einstiegsphase&quot;">​</a></h3></blockquote><p>Organisatorisches</p><ul><li>Klärung von offenen Fragen aus der vorherigen Vorlesung</li><li>Kursplanung/Strukturierung</li><li>Prüfungsleistungen</li><li>Fragen zu LiaScript:(Zugänge, Aufgaben etc.)</li></ul><blockquote><h3 id="arbeitsphase-1" tabindex="-1">Arbeitsphase <a class="header-anchor" href="#arbeitsphase-1" aria-label="Permalink to &quot;Arbeitsphase&quot;">​</a></h3></blockquote><p><strong>Wissenspool</strong></p><p>Die S. tragen in einem Wissenspool ihr Vorwissen aus der Selbstlernphase zum Thema „Was ist FDM?“ zusammen. Die S. (geleitet von L.) sollen einen konkreten Bezug zu ihrem Studium herstellen<br> Welche Rolle spielt FDM in ihrem Studium? In ihrem Fach?<br> Diskussion im Plenum über die Beiträge u. Erarbeitung neuer Stichpunkte</p><ul><li>L. leitet eine offene Diskussion zur Relevanz von FDM sowie Vor- und Nachteile im Plenum ein</li><li>L. gibt ggf. Beispiele aus eigener Erfahrung/aus der Fachdisziplin</li></ul><p><strong>Besprechung von Fallbeispielen</strong></p><p>Die S. sollen ein Bezug auf ihr Studium herstellen.<br> Diskussion und Ergänzung des Wissenspools<br> Übung mit DFG-Kodex. Anschließende Diskussion im Plenum<br> Arbeit in Kleingruppen und Beantwortung von vorbereiteten Fragen mit anschließender Diskussion im Plenum. </p><blockquote><h3 id="abschluss-1" tabindex="-1">Abschluss <a class="header-anchor" href="#abschluss-1" aria-label="Permalink to &quot;Abschluss&quot;">​</a></h3></blockquote><p>L. fragt die S., ob noch Unklarheiten bestehen oder offene Fragen vorhanden sind. L. gibt Ausblick auf nächstes Thema und erinnert an die (online) Vorbereitung zur nächsten Präsenzveranstaltung.</p></section><h2 id="mogliche-erganzende-materialien-im-lms" tabindex="-1">(Mögliche) Ergänzende Materialien im LMS <a class="header-anchor" href="#mogliche-erganzende-materialien-im-lms" aria-label="Permalink to &quot;(Mögliche) Ergänzende Materialien im LMS&quot;">​</a></h2><p>FDM-&quot;Schiffbrüche&quot; aus dem Forschungsalltag und was Sie daraus lernen können,<a href="https://zenodo.org/record/7377582" target="_blank" rel="noreferrer">https://zenodo.org/record/7377582</a></p><h2 id="taxonomiestufen" tabindex="-1">Taxonomiestufen <a class="header-anchor" href="#taxonomiestufen" aria-label="Permalink to &quot;Taxonomiestufen&quot;">​</a></h2><table><thead><tr><th></th><th></th><th></th></tr></thead><tbody><tr><td>Taxonomiestufe 1</td><td>Wissen, Kenntnisse (Tatsachen, Begriffe nennen)</td><td>Informationen möglichst wortgenau erinnern wiedergeben können, Informationen sinnerhaltend umformen und in eigenen Worten wiedergeben sowie zusammenfassen können</td></tr><tr><td>Taxonomiestufe 2</td><td>Verständnis (erklären, begründen, mit anderen Worten umschreiben)</td><td></td></tr><tr><td>Taxonomiestufe 3</td><td>Anwendung (Anwendung des Wissens, z.B. bei der Lösung eines praktischen Problems)</td><td>Abstraktionen (Regeln, Methoden etc.) in konkreten Situationen anwenden können</td></tr><tr><td>Taxonomiestufe 4</td><td>Analyse (eine reale Situation in ihre Elemente zerlegen)</td><td>Ideen, Problemstellungen in ihre Elemente zerlegen und vergleichen können</td></tr><tr><td>Taxonomiestufe 5</td><td>Synthese (aus verschiedenen Erlebnissen eine neue Situation konstruieren)</td><td>Einzelne Elemente zu einer Ganzheit formen</td></tr><tr><td>Taxonomiestufe 6</td><td>Bewertung (eine Situation beurteilen, überprüfen)</td><td>Ein bewertendes Urteil abgeben können</td></tr></tbody></table><h2 id="quellen" tabindex="-1">Quellen <a class="header-anchor" href="#quellen" aria-label="Permalink to &quot;Quellen&quot;">​</a></h2><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Lernziele Formulieren leicht gemacht</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/lernziele-formulieren-leicht-gemacht.htm" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/lernziele-formulieren-leicht-gemacht.htm</a>.</p><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Lernziele definieren</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/lernziele-definieren.html" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/lernziele-definieren.html</a>.</p><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Formulierungshilfen für Lernziele</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/formulierungshilfen-fur-lernziele.html" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/formulierungshilfen-fur-lernziele.html</a>.</p>',16),l=[r];function s(d,h,o,u,g,m){return n(),t("div",null,l)}const c=e(a,[["render",s]]);export{f as __pageData,c as default};
