import{_ as e,c as t,o as n,a3 as i}from"./chunks/framework.DA38T8Xw.js";const c=JSON.parse('{"title":"VL3: Datenlebenszyklus","description":"","frontmatter":{},"headers":[],"relativePath":"lz/VL_03_Datenlebenszyklus_LZ.md","filePath":"lz/VL_03_Datenlebenszyklus_LZ.md"}'),r={name:"lz/VL_03_Datenlebenszyklus_LZ.md"},a=i('<h1 id="vl3-datenlebenszyklus" tabindex="-1">VL3: Datenlebenszyklus <a class="header-anchor" href="#vl3-datenlebenszyklus" aria-label="Permalink to &quot;VL3: Datenlebenszyklus&quot;">​</a></h1><p><strong>Ausgangslage</strong></p><ul><li><p>Die S. sind mit dem Datenlebenszyklus nicht vertraut und kennen die unterschiedlichen Stationen.</p></li><li><p>Die S. können den Stationen beim Umgang mit Daten nicht folgen.</p></li></ul><h2 id="lernziele-fur-den-online-kurs" tabindex="-1">Lernziele für den Online-Kurs <a class="header-anchor" href="#lernziele-fur-den-online-kurs" aria-label="Permalink to &quot;Lernziele für den Online-Kurs&quot;">​</a></h2><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">1</td><td style="text-align:left;">Die S. können den Datenlebenszyklus beschreiben<a href="#14">(1)</a> und die unterschiedlichen Stationen mit deren wichtigsten Merkmalen aufzählen<a href="#14">(1)</a>.</td><td style="text-align:left;">Die S. verstehen den Datenlebenszyklus und welche Rolle dieser im FDM spielt/worauf zu achten ist.</td></tr><tr><td style="text-align:left;">2</td><td style="text-align:left;">Die S. können darlegen<a href="#14">(2)</a>, wie die unterschiedlichen Stationen des Datenlebenszyklus miteinander verknüpft sind.</td><td style="text-align:left;">Die S. setzen ihr erlerntes Wissen um, um die Phasen des Datenlebenszyklus mit deren Beschreibungen/Merkmalen/Inhalten zu verknüpfen.</td></tr></tbody></table><h2 id="lernziele-fur-den-unterricht-in-prasenz" tabindex="-1">Lernziele für den Unterricht in Präsenz <a class="header-anchor" href="#lernziele-fur-den-unterricht-in-prasenz" aria-label="Permalink to &quot;Lernziele für den Unterricht in Präsenz&quot;">​</a></h2><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">3</td><td style="text-align:left;">Die S. können detaillierte Beschreibungen den unterschiedlichen Stationen der Daten im Forschungsprozess korrekt zuordnen<a href="#14">(5)</a>.</td><td style="text-align:left;">Die S. setzen ihr erlerntes Wissen um, um die Phasen des Datenlebenszyklus mit deren Merkmalen und Inhalten zu verknüpfen.</td></tr><tr><td style="text-align:left;">4</td><td style="text-align:left;">Die S. können sich auf den Datenlebenszyklus als Leitfaden beim Umgang mit Daten beziehen<a href="#14">(5)</a> und für ein Beispielprojekt Arbeitsschritte einteilen<a href="#14">(4)</a> / konzipieren<a href="#14">(5)</a> und mit allen wichtigen Aspekte (z. B. korrektes Zitieren, Dokumentation, Richtlinien) verbinden<a href="#14">(5)</a>.</td><td style="text-align:left;">Die S. wissen, wie sie ausgehend vom Datenlebenszyklus ihre Arbeitsschritte beim Umgang mit Daten aufstellen und können beurteilen, was sie bei jeder Station beachten müssen.</td></tr><tr><td style="text-align:left;">5</td><td style="text-align:left;">Die S. sind in der Lage, die Daten, die sie in einem Projekt (z.B. Abschlussarbeit) während ihres Studiums erhoben haben, den unterschiedlichen Stationen des Datenlebenszyklus zuzuordnen<a href="#14">(5)</a>.</td><td style="text-align:left;">Ziel der Übung ist es, S. dafür zu sensibilisieren, die Phasen des Datenlebenszyklus, oder Ansätze davon, bereits in ihren (Projekt-)Arbeiten zu berücksichtigen.</td></tr></tbody></table><h2 id="detaillierte-kursplanung-1" tabindex="-1">Detaillierte Kursplanung[^1] <a class="header-anchor" href="#detaillierte-kursplanung-1" aria-label="Permalink to &quot;Detaillierte Kursplanung[^1]&quot;">​</a></h2><p><strong>Online-Phase (Vorbereitung)</strong></p><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden/ Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 30 min.</td><td>Einführung zum Datenlebenszyklus.<br><br>Beschreibung der Stationen und deren Merkmale.<br><br>Erläuterung, welche Rolle der DLZ im Forschungsprozess/ im FDM spielt.</td><td>Die S. können den Datenlebenszyklus beschreiben<a href="#14">(1)</a> und die unterschiedlichen Stationen mit deren wichtigsten Merkmalen aufzählen<a href="#14">(1)</a>.<br><br>Die S. können darlegen<a href="#14">(2)</a>, wie die unterschiedlichen Stationen des Datenlebenszyklus miteinander verknüpft sind.</td><td>Text und Erklärvideo als Informationsquelle.<br><br>Interaktiver Datenlebenszyklus.<br><br>Beantworten von zwei Leitfragen (unten) im Lernportfolio.</td><td>EA</td></tr></tbody></table><hr><p>[^1]:<strong>Legende der Sozialformen:</strong> PL = Plenum; L = Lehrende*r; S = Studierende; LV = Lehrvortrag; EA = Einzelarbeit, GA = Gruppenarbeit.</p><h2 id="prasenzphase" tabindex="-1">Präsenzphase <a class="header-anchor" href="#prasenzphase" aria-label="Permalink to &quot;Präsenzphase&quot;">​</a></h2><h3 id="einstiegsphase" tabindex="-1">Einstiegsphase <a class="header-anchor" href="#einstiegsphase" aria-label="Permalink to &quot;Einstiegsphase&quot;">​</a></h3><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden/ Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 5 min.</td><td>Organisatorisches</td><td></td><td></td><td></td></tr><tr><td>Ca. 15 min.</td><td>„Ice Breaker“<br><br>Die S. besprechen, welche Stationen (Phasen) des DLZ ihnen im Studium begegnet sind.</td><td>Reflexion des erlernten Wissens. Bezug auf bisherige Erfahrung im Studium.</td><td>Erfahrungsaustausch und Diskussion</td><td>PL</td></tr></tbody></table><h3 id="arbeitsphase" tabindex="-1">Arbeitsphase <a class="header-anchor" href="#arbeitsphase" aria-label="Permalink to &quot;Arbeitsphase&quot;">​</a></h3><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden/ Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 45 min.</td><td>Gruppenarbeit<br><br>Die S. arbeiten zusammen in Gruppen an einem Beispielprojekt</td><td>Die S. können sich auf den Datenlebenszyklus als Leitfaden beim Umgang mit Daten beziehen(5) und für ein Beispielprojekt Arbeitsschritte einteilen<a href="#14">(4)</a> / konzipieren<a href="#14">(5)</a> und mit allen wichtigen Aspekte (z. B. korrektes Zitieren, Dokumentation, Richtlinien) verbinden<a href="#14">(5)</a>.</td><td>L. teilt die S. in Gruppen ein. Das Beispiel wird von L. vorgegeben.<br><br>Die Gruppen müssen dann jeweils zusammenarbeiten, um die Aufgabe zu erledigen.<br><br>Die S. müssen bestimmte Fragen beantworten.(Fragen sind in der Phasen-Beschreibung unten notiert).<br><br>Danach leitet die L. eine offene Diskussion ein und bittet die Gruppen, ihre Arbeit vorzustellen.</td><td>GA / PL</td></tr><tr><td>Ca. 20 min.</td><td>Diskussion<br><br>Die S. legen Beispiele dar, indem sie sich auf Projekte aus ihrem Fach/Studium beziehen.</td><td>Die S. können detaillierte Beschreibungen den unterschiedlichen Stationen des Datenlebenszyklus korrekt zuordnen<a href="#14">(5)</a>.<br><br>Die S. sind in der Lage, in ihrem Studium die unterschiedlichen Stationen des Datenlebenszyklus für ein Beispielprojekt (z. B. Abschlussarbeit) mit Daten zuzuordnen<a href="#14">(5)</a>.</td><td>L. leitet eine Diskussion im PL.<br><br>Reflexion des Themas in Bezug auf das Studium:<br><br>S. sollen überlegen und begründen, welche Rolle der Datenlebenszyklus in ihrem Studium (z.B. Für Projekt- oder Abschlussarbeiten) spielt.</td><td>PL</td></tr></tbody></table><h3 id="abschluss" tabindex="-1">Abschluss <a class="header-anchor" href="#abschluss" aria-label="Permalink to &quot;Abschluss&quot;">​</a></h3><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden/ Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 5. min.</td><td>Offene Fragen / Unklarheiten klären Ausblick auf und Arbeitsauftrag für nächste Präsenzveranstaltung</td><td>L. erklärt die Erwartungen für nächste Stunde. L. bietet S. an, offene Fragen zum Vorlesungsthema, dem Seminarverlauf und/oder anderen Aspekten zu besprechen.</td><td></td><td>PL</td></tr></tbody></table><h2 id="prasenzphase-erlauterungen" tabindex="-1">Präsenzphase / Erläuterungen <a class="header-anchor" href="#prasenzphase-erlauterungen" aria-label="Permalink to &quot;Präsenzphase / Erläuterungen&quot;">​</a></h2><h3 id="einstiegsphase-1" tabindex="-1">Einstiegsphase <a class="header-anchor" href="#einstiegsphase-1" aria-label="Permalink to &quot;Einstiegsphase&quot;">​</a></h3><h1 id="ca-5-min-organisatorisches" tabindex="-1">(ca. 5 min.) Organisatorisches <a class="header-anchor" href="#ca-5-min-organisatorisches" aria-label="Permalink to &quot;(ca. 5 min.) Organisatorisches&quot;">​</a></h1><p>Klärung für offene Fragen zum Kurs, LiaScript oder den Prüfungsleistungen</p><h1 id="ca-15-min-ice-breaker" tabindex="-1">(ca. 15 min.) Ice Breaker <a class="header-anchor" href="#ca-15-min-ice-breaker" aria-label="Permalink to &quot;(ca. 15 min.) Ice Breaker&quot;">​</a></h1><p>Erfahrungsaustausch und Diskussion</p><p>Reflexion des auf LiaScript erlernten Wissens mit Bezug auf die bisherige Erfahrung im Studium.</p><p>Die S. besprechen, welche Stationen (Phasen) des DLZ ihnen im Studium begegnet sind.</p><h3 id="arbeitsphase-1" tabindex="-1">Arbeitsphase <a class="header-anchor" href="#arbeitsphase-1" aria-label="Permalink to &quot;Arbeitsphase&quot;">​</a></h3><h1 id="ca-45-min-methode-gruppenarbeit" tabindex="-1">(ca. 45 min.) Methode: Gruppenarbeit <a class="header-anchor" href="#ca-45-min-methode-gruppenarbeit" aria-label="Permalink to &quot;(ca. 45 min.) Methode: Gruppenarbeit&quot;">​</a></h1><p>L. teilt die S. in Gruppen ein und erläutert den Arbeitsauftrag: Die Gruppen bekommen ein Beispielprojekt und sollen dieses anhand von gezielten Fragen in Arbeitsschritte einteilen und mit allen wichtigen Aspekten (z. B. korrektes Zitieren, Dokumentation etc.) zum DLZ und FDM verbinden.</p><p>S. sollen für das Beispielprojekt folgendes beantworten:</p><ul><li>Mit welchen Daten wurde gearbeitet?</li><li>Wurden Daten selbst erhoben oder wurden existierende Daten nachgenutzt?</li><li>Wie wurden die Daten aufbereitet/verarbeitet?</li><li>Wo wurden die Daten gespeichert?</li><li>Wer hat mit diesen Daten / an diesem Projekt gearbeitet?</li><li>Wurden die Daten archiviert?</li><li>Wurden die Daten veröffentlicht?</li><li>Können die Daten nachgenutzt werden?</li><li>Existiert eine Dokumentation der Arbeitsschritte und der wichtigen Aspekte für die Daten? Wieso könnte das wichtig sein?</li><li>Können die Daten zitiert werden?</li></ul><p>Anschließende Diskussion im Plenum. Unter Moderation von L. werden die einzelnen Aspekte bzw. Ergebnisse diskutiert.</p><h1 id="ca-20-min-reflexion-im-plenum" tabindex="-1">(ca. 20 min.) Reflexion im Plenum <a class="header-anchor" href="#ca-20-min-reflexion-im-plenum" aria-label="Permalink to &quot;(ca. 20 min.) Reflexion im Plenum&quot;">​</a></h1><p>Reflexion des Themas in Bezug auf das Studium: Die S. sollen überlegen und begründen, welche Rolle der Datenlebenszyklus in ihrem Studium (z.B. für Projekt- oder Abschlussarbeiten) spielt.</p><h3 id="abschluss-1" tabindex="-1">Abschluss <a class="header-anchor" href="#abschluss-1" aria-label="Permalink to &quot;Abschluss&quot;">​</a></h3><p>L. fragt die S., ob noch Unklarheiten bestehen oder offene Fragen vorhanden sind.</p><p>L. gibt Ausblick auf nächstes Thema und erinnert an die (online) Vorbereitung zur nächsten Präsenzveranstaltung.</p><h2 id="taxonomiestufen" tabindex="-1">Taxonomiestufen <a class="header-anchor" href="#taxonomiestufen" aria-label="Permalink to &quot;Taxonomiestufen&quot;">​</a></h2><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">Taxonomiestufe 1</td><td style="text-align:left;">Wissen, Kenntnisse (Tatsachen, Begriffe nennen)</td><td style="text-align:left;">Informationen möglichst wortgenau erinnern wiedergeben können, Informationen sinnerhaltend umformen und in eigenen Worten wiedergeben sowie zusammenfassen können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 2</td><td style="text-align:left;">Verständnis (erklären, begründen, mit anderen Worten umschreiben)</td><td style="text-align:left;"></td></tr><tr><td style="text-align:left;">Taxonomiestufe 3</td><td style="text-align:left;">Anwendung (Anwendung des Wissens, z.B. bei der Lösung eines praktischen Problems)</td><td style="text-align:left;">Abstraktionen (Regeln, Methoden etc.) in konkreten Situationen anwenden können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 4</td><td style="text-align:left;">Analyse (eine reale Situation in ihre Elemente zerlegen)</td><td style="text-align:left;">Ideen, Problemstellungen in ihre Elemente zerlegen und vergleichen können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 5</td><td style="text-align:left;">Synthese (aus verschiedenen Erlebnissen eine neue Situation konstruieren)</td><td style="text-align:left;">Einzelne Elemente zu einer Ganzheit formen</td></tr><tr><td style="text-align:left;">Taxonomiestufe 6</td><td style="text-align:left;">Bewertung (eine Situation beurteilen, überprüfen)</td><td style="text-align:left;">Ein bewertendes Urteil abgeben können</td></tr></tbody></table><h2 id="quellen" tabindex="-1">Quellen: <a class="header-anchor" href="#quellen" aria-label="Permalink to &quot;Quellen:&quot;">​</a></h2><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Lernziele Formulieren leicht gemacht</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/lernziele-formulieren-leicht-gemacht.htm" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/lernziele-formulieren-leicht-gemacht.htm</a></p><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Lernziele definieren</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/lernziele-definieren.html" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/lernziele-definieren.html</a></p><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Formulierungshilfen für Lernziele</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/formulierungshilfen-fur-lernziele.html" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/formulierungshilfen-fur-lernziele.html</a></p>',44),l=[a];function s(d,h,u,o,b,m){return n(),t("div",null,l)}const g=e(r,[["render",s]]);export{c as __pageData,g as default};
