import{_ as e,c as t,o as n,a3 as i}from"./chunks/framework.8CaK9a7u.js";const f=JSON.parse('{"title":"VL4: FAIR-Prinzipien","description":"","frontmatter":{},"headers":[],"relativePath":"lz/VL_04_FAIR_LZ.md","filePath":"lz/VL_04_FAIR_LZ.md"}'),r={name:"lz/VL_04_FAIR_LZ.md"},a=i('<h1 id="vl4-fair-prinzipien" tabindex="-1">VL4: FAIR-Prinzipien <a class="header-anchor" href="#vl4-fair-prinzipien" aria-label="Permalink to &quot;VL4: FAIR-Prinzipien&quot;">​</a></h1><p><strong>Ausgangslage</strong></p><ul><li>Das Bewusstsein für FAIR-Prinzipien ist bei den S. nicht vorhanden.</li><li>FAIR-Prinzipien sind im BA-Level anwendbar – insbesondere Findable (Auffindbar) und Reusable (Nachnutzbar) –, aber werden nicht geprüft.</li></ul><h2 id="lernziele-fur-den-online-kurs" tabindex="-1">Lernziele für den Online-Kurs <a class="header-anchor" href="#lernziele-fur-den-online-kurs" aria-label="Permalink to &quot;Lernziele für den Online-Kurs&quot;">​</a></h2><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">1</td><td style="text-align:left;">Die S. können die FAIR-Prinzipien benennen(1) und die einzelnen Aspekte beschreiben(1).</td><td style="text-align:left;">Die S. wissen, wofür FAIR steht, was die Merkmale der Prinzipien sind und warum deren Beachtung im Forschungsprozess Vorteile hat.</td></tr><tr><td style="text-align:left;">Lernziele für den Unterricht in Präsenz</td><td style="text-align:left;"></td><td style="text-align:left;"></td></tr><tr><td style="text-align:left;">2</td><td style="text-align:left;">Die S. können die Vor- und Nachteile von fairen Daten gegenüberstellen(4).</td><td style="text-align:left;">Die S. wissen, was die Merkmale der Prinzipien sind und können Vor- und Nachteile/Hindernisse für den Forschungsprozess extrapolieren.</td></tr><tr><td style="text-align:left;">3</td><td style="text-align:left;">Die S. sind in der Lage, die Fairness von Daten zu beurteilen (6).</td><td style="text-align:left;">Die S. können anhand von Beispielen erkennen, welche Forschungsprozesse die FAIR-Prinzipien beachten/erfüllen und welche nicht. Außerdem können sie ihr erlerntes Wissen auf ihr Studium übertragen, um zu einem späteren Zeitpunkt, die FAIR-Prinzipien in Projektarbeit (mit Daten) beachten zu können.</td></tr><tr><td style="text-align:left;">4</td><td style="text-align:left;">Die S. sind in der Lage, ihre Dateien (für ein Beispielprojekt) FAIR (für sich und für Dritte) zusammenzustellen(5).</td><td style="text-align:left;">Die S. können anhand von Beispielen erkennen, welche Forschungsprozesse die FAIR-Prinzipien beachten/erfüllen und welche nicht. Außerdem können sie ihr erlerntes Wissen auf ihr Studium übertragen, um zu einem späteren Zeitpunkt, die FAIR-Prinzipien in Projektarbeiten (mit Daten) beachten zu können.</td></tr></tbody></table><h2 id="detaillierte-kursplanung-1" tabindex="-1">Detaillierte Kursplanung[^1] <a class="header-anchor" href="#detaillierte-kursplanung-1" aria-label="Permalink to &quot;Detaillierte Kursplanung[^1]&quot;">​</a></h2><p><strong>Online-Kurs</strong></p><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden/ Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 20 min.</td><td>Einführung in das Thema: Infotext zu FAIR-Prinzipien. Aufschlüsselung des Akronyms, Bezug zu Leitlinie in der Hochschule, Relevanz von FDM <br> <br> Optional: <br> Video von GOfair, <br> Best-Practice-Beispiel “Fairytale”, <br> Quiz <br></td><td>Die S. können die FAIR-Prinzipien benennen (1) und die einzelnen Aspekte beschreiben (1).</td><td>Text <br> Optional: Erklärvideo, Best-Practice-Beispiel, Quiz</td><td>EA</td></tr><tr><td>Ca. 10 min.</td><td>Leitfragen zur Vorbereitung für Präsenzphase</td><td></td><td>Schriftliche Beantwortung der Leitfragen im Lernportfolio zur Vertiefung und Vorbereitung auf Präsenzphase (Transferaufgabe)</td><td>EA</td></tr></tbody></table><hr><p>[^1]:<strong>Legende der Sozialformen:</strong> PL = Plenum; L = Lehrende*r; S = Studierende; LV = Lehrvortrag; EA = Einzelarbeit, GA = Gruppenarbeit.</p><h3 id="prasenzphase" tabindex="-1">Präsenzphase <a class="header-anchor" href="#prasenzphase" aria-label="Permalink to &quot;Präsenzphase&quot;">​</a></h3><h4 id="einstiegsphase" tabindex="-1">Einstiegsphase <a class="header-anchor" href="#einstiegsphase" aria-label="Permalink to &quot;Einstiegsphase&quot;">​</a></h4><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden/ Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 5 min.</td><td>Begrüßung u. Vorstellung der zeitlichen und thematischen Agenda</td><td>L. bietet Orientierung</td><td></td><td>LV</td></tr><tr><td>Ca. 5 min.</td><td>Mindmap zu Vor- und Nachteilen von FAIR-Prinzipien</td><td>Die S. können die Vor- und Nachteile von fairen Daten gegenüberstellen(4).</td><td>Wissenspool oder: Pro-Kontra-Diskussion mit zufälliger Einteilung der S. in eine Pro- und eine Kontragruppe, für die es jeweils Argumente zu finden gilt.</td><td>PL</td></tr><tr><td>Ca. 5 min.</td><td>Tandem: „Wie lassen sich die FAIR-Prinzipien auf Ihr Studium übertragen?“</td><td>Ergebnissicherung, Vertiefung der Inhalte aus der Online-Phase. Die S. können Ihre schriftlichen Ausarbeitungen ergänzen. Die S. werden aktiviert und können ihr vorhandenes Vorwissen einbringen und gleichzeitig assoziatives Denken üben.</td><td>Think-Pair-Share im Tandem</td><td>GA</td></tr><tr><td>Ca. 10 min.</td><td>Diskussion im Plenum über festgehaltene Beiträge</td><td>s. o.</td><td>Diskussion</td><td>PL</td></tr></tbody></table><h4 id="arbeitsphase" tabindex="-1">Arbeitsphase <a class="header-anchor" href="#arbeitsphase" aria-label="Permalink to &quot;Arbeitsphase&quot;">​</a></h4><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden/ Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 20 min.</td><td>Gruppeneinteilung Gruppen-Aufgabe zu „Fairness von Daten“.</td><td>Jede Gruppe sucht sich einen Datensatz. Die S. besprechen und erarbeiten jeweils ein Fallbeispiel anhand von Fragen in Gruppenarbeit (insgesamt 3 Gruppen). alternativ: Kopfstandmethode - Wie mache ich meine Daten möglichst unfair? Die S. sind in der Lage, ihre Dateien (für ein Beispielprojekt) fair (für sich und für Dritte) zusammenzustellen(5). Die S. sind in der Lage, die Fairness von Daten zu beurteilen(6). - Vertiefung und Festigung der Inhalte durch Transferaufgabe und Anwendungsbezug</td><td>Erarbeitung von Fallbeispielen in Gruppenarbeit Vorbereitung von Impulsvorträgen</td><td>GA</td></tr><tr><td>Ca. 40 min.</td><td>Präsentation der einzelnen Gruppen (jeweils ca. 5 Min. pro Gruppe) mit jeweils anschließender Diskussion im Plenum (jeweils ca. 10 Min.)</td><td>Ergebnissicherung</td><td>Impulsvorträge der Gruppen und Plenumsdiskussion</td><td>PL</td></tr></tbody></table><h4 id="abschluss" tabindex="-1">Abschluss <a class="header-anchor" href="#abschluss" aria-label="Permalink to &quot;Abschluss&quot;">​</a></h4><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden/ Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 10 min.</td><td>Offene Fragen / Unklarheiten klären Ausblick auf und Arbeitsauftrag für nächste Präsenzveranstaltung</td><td>L. erklärt Erwartungen für nächste Stunde L. bietet S. an, offene Fragen zum Vorlesungsthema, dem Seminarverlauf und/oder anderen Aspekten zu besprechen.</td><td></td><td>PL</td></tr></tbody></table><h2 id="prasenzphase-erlauterungen" tabindex="-1">Präsenzphase / Erläuterungen <a class="header-anchor" href="#prasenzphase-erlauterungen" aria-label="Permalink to &quot;Präsenzphase / Erläuterungen&quot;">​</a></h2><h3 id="einstiegsphase-1" tabindex="-1">Einstiegsphase <a class="header-anchor" href="#einstiegsphase-1" aria-label="Permalink to &quot;Einstiegsphase&quot;">​</a></h3><p>Begrüßung</p><p>(ca. 5 min.) Methode: Wissenspool (Mindmap)*</p><h1 id="aufgabe" tabindex="-1">Aufgabe: <a class="header-anchor" href="#aufgabe" aria-label="Permalink to &quot;Aufgabe:&quot;">​</a></h1><p>Benennen Sie die Vor- und Nachteile von FAIR-Prinzipien</p><p>* Alternativ: Pro-Kontra-Diskussion mit zufälliger Einteilung der S. in eine Pro- und eine Kontragruppe, für die es jeweils Argumente zu finden gilt.</p><p>Weiterführende Fragen, z. B.</p><ul><li>Was ist mit personenbezogenen Daten?</li><li>Was ist mit Geschäftsgeheimnissen / Geheimhaltung / Patente?</li><li>Wenn ich mich aus eigenem Interesse dagegen entscheide?</li></ul><p>(ca. 5 min.) Methode: Think – Pair – Share</p><h1 id="aufgabe-1" tabindex="-1">Aufgabe: <a class="header-anchor" href="#aufgabe-1" aria-label="Permalink to &quot;Aufgabe:&quot;">​</a></h1><p>Wie lassen sich die FAIR-Prinzipien auf Ihr Studium übertragen?</p><p>(ca. 10 min.) Methode: Diskussion im Plenum über festgehaltene Beiträge</p><h3 id="arbeitsphase-1" tabindex="-1">Arbeitsphase <a class="header-anchor" href="#arbeitsphase-1" aria-label="Permalink to &quot;Arbeitsphase&quot;">​</a></h3><p>L. teilt die S. in Gruppen von 3 bis 5 S. ein und erläutert den Arbeitsauftrag:</p><p>Jede Gruppe sucht sich einen Datensatz. Die einzelnen Fallbeispiele sollen die jeweiligen Gruppen besprechen (20 min.) und anhand folgender Fragen so ausarbeiten, dass sie sie dem Plenum im Anschluss in 5 Min. vorstellen können:</p><ul><li>Bei welchen Aspekten wurden die FAIR-Prinzipien berücksichtigt?</li><li>Wie können die unFAIRen Aspekte verbessert werden?</li></ul><p>Besprechung von Fallbeispielen mit anschließender Diskussion (pro Gruppe ca. 10 min. Vorstellung + ca. 10 min. Diskussion)</p><p>* Alternativ: Kopfstandmethode - Wie mache ich meine Daten möglichst unfair? Diskussion im Plenum.</p><h3 id="abschluss-1" tabindex="-1">Abschluss <a class="header-anchor" href="#abschluss-1" aria-label="Permalink to &quot;Abschluss&quot;">​</a></h3><p>L. fragt die S., ob noch Unklarheiten bestehen oder offene Fragen vorhanden sind.</p><p>L. gibt Ausblick auf nächstes Thema und erinnert an die (online) Vorbereitung zur nächsten Präsenzveranstaltung.</p><h2 id="mogliche-erganzende-materialien-im-lms" tabindex="-1">(Mögliche) Ergänzende Materialien im LMS <a class="header-anchor" href="#mogliche-erganzende-materialien-im-lms" aria-label="Permalink to &quot;(Mögliche) Ergänzende Materialien im LMS&quot;">​</a></h2><p>Folien 10-15: Susanne, Fliegner, Angelika, &amp; Raatz, Philip. (2023, April 14). <em>#datendienstag: Datenmanagementpläne und Forschungsdatenmanagement in Forschungsanträgen</em>. Zenodo. <a href="https://doi.org/10.5281/zenodo.7825751" target="_blank" rel="noreferrer">https://doi.org/10.5281/zenodo.7825751</a></p><p>Angaben zum Umgang mit Forschungsdaten. → Hier gibt es oft Leitlinien (z. B. von Institutionen, wie der TH Köln), denen gefolgt werden kann. <a href="https://forschungsdaten-thueringen.de/files/images/Material/2023_02_14_Farify_your_data.pdf" target="_blank" rel="noreferrer">https://forschungsdaten-thueringen.de/files/images/Material/2023_02_14_Farify_your_data.pdf</a></p><p>GO FAIR – FAIR made easy (englisch, 4:06), <a href="https://youtu.be/CEPsTiEgWR4" target="_blank" rel="noreferrer">https://youtu.be/CEPsTiEgWR4</a></p><h2 id="taxonomiestufen" tabindex="-1">Taxonomiestufen <a class="header-anchor" href="#taxonomiestufen" aria-label="Permalink to &quot;Taxonomiestufen&quot;">​</a></h2><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">Taxonomiestufe 1</td><td style="text-align:left;">Wissen, Kenntnisse (Tatsachen, Begriffe nennen)</td><td style="text-align:left;">Informationen möglichst wortgenau erinnern wiedergeben können, Informationen sinnerhaltend umformen und in eigenen Worten wiedergeben sowie zusammenfassen können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 2</td><td style="text-align:left;">Verständnis (erklären, begründen, mit anderen Worten umschreiben)</td><td style="text-align:left;"></td></tr><tr><td style="text-align:left;">Taxonomiestufe 3</td><td style="text-align:left;">Anwendung (Anwendung des Wissens, z.B. bei der Lösung eines praktischen Problems)</td><td style="text-align:left;">Abstraktionen (Regeln, Methoden etc.) in konkreten Situationen anwenden können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 4</td><td style="text-align:left;">Analyse (eine reale Situation in ihre Elemente zerlegen)</td><td style="text-align:left;">Ideen, Problemstellungen in ihre Elemente zerlegen und vergleichen können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 5</td><td style="text-align:left;">Synthese (aus verschiedenen Erlebnissen eine neue Situation konstruieren)</td><td style="text-align:left;">Einzelne Elemente zu einer Ganzheit formen</td></tr><tr><td style="text-align:left;">Taxonomiestufe 6</td><td style="text-align:left;">Bewertung (eine Situation beurteilen, überprüfen)</td><td style="text-align:left;">Ein bewertendes Urteil abgeben können</td></tr></tbody></table><h2 id="quellen" tabindex="-1">Quellen: <a class="header-anchor" href="#quellen" aria-label="Permalink to &quot;Quellen:&quot;">​</a></h2><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Lernziele Formulieren leicht gemacht</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/lernziele-formulieren-leicht-gemacht.htm" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/lernziele-formulieren-leicht-gemacht.htm</a></p><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Lernziele definieren</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/lernziele-definieren.html" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/lernziele-definieren.html</a></p><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Formulierungshilfen für Lernziele</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/formulierungshilfen-fur-lernziele.html" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/formulierungshilfen-fur-lernziele.html</a></p>',49),s=[a];function l(d,h,u,o,g,p){return n(),t("div",null,s)}const m=e(r,[["render",l]]);export{f as __pageData,m as default};
