import{_ as e,c as t,o as n,a3 as r}from"./chunks/framework.Dcugq_a2.js";const m=JSON.parse('{"title":"VL5: Dokumentation von Daten","description":"","frontmatter":{},"headers":[],"relativePath":"lz/VL_05_DMP_LZ.md","filePath":"lz/VL_05_DMP_LZ.md"}'),a={name:"lz/VL_05_DMP_LZ.md"},i=r('<h1 id="vl5-dokumentation-von-daten" tabindex="-1">VL5: Dokumentation von Daten <a class="header-anchor" href="#vl5-dokumentation-von-daten" aria-label="Permalink to &quot;VL5: Dokumentation von Daten&quot;">​</a></h1><p><strong>Ausgangslage</strong></p><ul><li><p>Die S. haben wenige bis keine Erfahrung beim Dokumentieren von Daten.</p></li><li><p>Die S. wissen nicht, worauf sie beim Umgang mit Daten achten müssen.</p></li></ul><h2 id="lernziele-fur-den-online-kurs" tabindex="-1">Lernziele für den Online-Kurs <a class="header-anchor" href="#lernziele-fur-den-online-kurs" aria-label="Permalink to &quot;Lernziele für den Online-Kurs&quot;">​</a></h2><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">1</td><td style="text-align:left;">Die S. können beschreiben (1), was der Nutzen einer Dokumentation von Daten ist und herausstellen (2), welche Relevanz dies hat.</td><td style="text-align:left;">Die S. haben gelernt, welche Komponenten eine Dokumentation von Daten enthalten kann, und weshalb diese so wichtig ist.</td></tr><tr><td style="text-align:left;">2</td><td style="text-align:left;">Die S. können herausstellen (2), welche Aspekte eines Forschungsprozesses/-projektes für die Dokumentation relevant und wichtig sind.</td><td style="text-align:left;">Die S. verstehen die Korrelation zwischen und Relevanz von Forschungsprojekten und deren Dokumentation.</td></tr></tbody></table><h2 id="lernziele-fur-den-unterricht-in-prasenz" tabindex="-1">Lernziele für den Unterricht in Präsenz <a class="header-anchor" href="#lernziele-fur-den-unterricht-in-prasenz" aria-label="Permalink to &quot;Lernziele für den Unterricht in Präsenz&quot;">​</a></h2><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">3</td><td style="text-align:left;">Die S. sind in der Lage, für ein Beispielprojekt Aspekte im Umgang mit Daten korrekt zu tabellieren (5) und ihre Entscheidung zu begründen (6).</td><td style="text-align:left;">Die S. lernen so, das angeeignete Wissen im Nachgang für (Daten- )Projekte in ihrem Studium anzuwenden und Daten zu dokumentieren.</td></tr></tbody></table><h2 id="detaillierte-kursplanung-1" tabindex="-1">Detaillierte Kursplanung[^1] <a class="header-anchor" href="#detaillierte-kursplanung-1" aria-label="Permalink to &quot;Detaillierte Kursplanung[^1]&quot;">​</a></h2><p><strong>Online-Kurs</strong></p><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden / Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 20 min.</td><td>Einführung in das Thema:<br><br>Infotext<br><br>Video:<br><br>Hilfsmittel Checkliste</td><td>Die S. können beschreiben(1), was der Nutzen des Dokumentierens von Daten ist und herausstellen(2), welche Relevanz dies hat.<br><br>Die S. können herausstellen(2), welche Aspekte eines Forschungsprozesses/-projektes für die Dokumentation relevant und wichtig sind.</td><td>Text und Erklärvideo als Informationsquelle<br><br> Hilfsmittel Checkliste</td><td>EA</td></tr><tr><td>Ca. 10 min.</td><td>Leitfragen zur Vorbereitung für Präsenzphase</td><td>Transferaufgabe</td><td>Schriftliche Beantwortung der Leitfragen im Lernportfolio zur Vertiefung und Vorbereitung auf Präsenzphase</td><td>EA</td></tr></tbody></table><hr><p>[^1]:<strong>Legende der Sozialformen:</strong> PL = Plenum; L = Lehrende*r; S = Studierende; LV = Lehrvortrag; EA = Einzelarbeit, GA = Gruppenarbeit.</p><h3 id="prasenzphase" tabindex="-1">Präsenzphase <a class="header-anchor" href="#prasenzphase" aria-label="Permalink to &quot;Präsenzphase&quot;">​</a></h3><h4 id="einstiegsphase" tabindex="-1">Einstiegsphase <a class="header-anchor" href="#einstiegsphase" aria-label="Permalink to &quot;Einstiegsphase&quot;">​</a></h4><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden / Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 5 min.</td><td>Begrüßung u. Vorstellung der zeitlichen und thematischen Agenda</td><td>L. bietet Orientierung</td><td></td><td>LV</td></tr></tbody></table><h4 id="arbeitsphase" tabindex="-1">Arbeitsphase <a class="header-anchor" href="#arbeitsphase" aria-label="Permalink to &quot;Arbeitsphase&quot;">​</a></h4><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden / Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 70 min.</td><td>Gruppeneinteilung<br><br>Gruppen-Aufgabe zu „Dokumentation von Daten“<br><br>L. berät die Gruppen bei Herausforderungen und Schwierigkeiten</td><td>Die S. besprechen und erarbeiten jeweils ein Fallbeispiel in Gruppenarbeit<br><br>Die S. sind in der Lage, für ein Beispielprojekt Aspekte im Umgang mit Daten korrekt zu tabellieren(5) und ihre Entscheidung zu begründen(6).<br> Festigung der Inhalte durch Transferaufgabe und Anwendungsbezug</td><td>Erarbeitung von Fallbeispielen in Gruppenarbeit</td><td>GA</td></tr></tbody></table><h4 id="abschluss" tabindex="-1">Abschluss <a class="header-anchor" href="#abschluss" aria-label="Permalink to &quot;Abschluss&quot;">​</a></h4><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden / Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 10 min.</td><td>Reflexion über Gruppenarbeit (Herausforderungen, Nachteile, Vorteile, Chancen etc.) im Plenum</td><td>Reflexion</td><td>Plenumsdiskussion</td><td>PL</td></tr><tr><td>Ca. 5 min.</td><td>Offene Fragen / Unklarheiten klären Ausblick auf und Arbeitsauftrag für nächste Präsenzveranstaltung</td><td>L. erklärt Erwartungen für nächste Stunde.<br><br>L. bietet S. an, offene Fragen zum Vorlesungsthema, dem Seminarverlauf und/oder anderen Aspekten zu besprechen.</td><td></td><td>PL</td></tr></tbody></table><h2 id="prasenzphase-1" tabindex="-1">Präsenzphase <a class="header-anchor" href="#prasenzphase-1" aria-label="Permalink to &quot;Präsenzphase&quot;">​</a></h2><h3 id="einstiegsphase-1" tabindex="-1">Einstiegsphase <a class="header-anchor" href="#einstiegsphase-1" aria-label="Permalink to &quot;Einstiegsphase&quot;">​</a></h3><p>Begrüßung</p><h3 id="arbeitsphase-1" tabindex="-1">Arbeitsphase <a class="header-anchor" href="#arbeitsphase-1" aria-label="Permalink to &quot;Arbeitsphase&quot;">​</a></h3><p>(ca. 70 min.) Gruppenaufgabe zu „Dokumentation von Daten / DMP“</p><p>L. teilt S. in Gruppen ein und erläutert den Arbeitsauftrag:</p><p>Überlegen Sie sich eine Datenmanagement-Strategie für einen eigenen Datensatz (z. B. Vorlesungsmitschriften, Musiksammlung o. Ä.).</p><ul><li>Wie würden Sie vorgehen?</li><li>Welche Schritte sind nötig?</li><li>Versuchen Sie, die Checkliste (Hilfsmittel) auszufüllen.</li></ul><p>L. berät die Gruppen bei Herausforderungen und Schwierigkeiten.</p><h3 id="abschluss-1" tabindex="-1">Abschluss <a class="header-anchor" href="#abschluss-1" aria-label="Permalink to &quot;Abschluss&quot;">​</a></h3><p>(10 min.) Reflexion über Gruppenarbeit (Herausforderungen, Nachteile, Vorteile, Chancen etc.) im Plenum.</p><p>L. fragt S., ob noch Unklarheiten bestehen oder offene Fragen vorhanden sind.</p><p>L. gibt Ausblick auf nächstes Thema und erinnert an die (online) Vorbereitung zur nächsten Präsenzveranstaltung.</p><h2 id="taxonomiestufen" tabindex="-1">Taxonomiestufen <a class="header-anchor" href="#taxonomiestufen" aria-label="Permalink to &quot;Taxonomiestufen&quot;">​</a></h2><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">Taxonomiestufe 1</td><td style="text-align:left;">Wissen, Kenntnisse (Tatsachen, Begriffe nennen)</td><td style="text-align:left;">Informationen möglichst wortgenau erinnern wiedergeben können, Informationen sinnerhaltend umformen und in eigenen Worten wiedergeben sowie zusammenfassen können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 2</td><td style="text-align:left;">Verständnis (erklären, begründen, mit anderen Worten umschreiben)</td><td style="text-align:left;"></td></tr><tr><td style="text-align:left;">Taxonomiestufe 3</td><td style="text-align:left;">Anwendung (Anwendung des Wissens, z.B. bei der Lösung eines praktischen Problems)</td><td style="text-align:left;">Abstraktionen (Regeln, Methoden etc.) in konkreten Situationen anwenden können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 4</td><td style="text-align:left;">Analyse (eine reale Situation in ihre Elemente zerlegen)</td><td style="text-align:left;">Ideen, Problemstellungen in ihre Elemente zerlegen und vergleichen können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 5</td><td style="text-align:left;">Synthese (aus verschiedenen Erlebnissen eine neue Situation konstruieren)</td><td style="text-align:left;">Einzelne Elemente zu einer Ganzheit formen</td></tr><tr><td style="text-align:left;">Taxonomiestufe 6</td><td style="text-align:left;">Bewertung (eine Situation beurteilen, überprüfen)</td><td style="text-align:left;">Ein bewertendes Urteil abgeben können</td></tr></tbody></table><h2 id="quellen" tabindex="-1">Quellen: <a class="header-anchor" href="#quellen" aria-label="Permalink to &quot;Quellen:&quot;">​</a></h2><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Lernziele Formulieren leicht gemacht</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/lernziele-formulieren-leicht-gemacht.htm" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/lernziele-formulieren-leicht-gemacht.htm</a></p><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Lernziele definieren</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/lernziele-definieren.html" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/lernziele-definieren.html</a></p><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Formulierungshilfen für Lernziele</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/formulierungshilfen-fur-lernziele.html" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/formulierungshilfen-fur-lernziele.html</a></p>',38),l=[i];function s(d,h,o,u,b,g){return n(),t("div",null,l)}const c=e(a,[["render",s]]);export{m as __pageData,c as default};
