import{_ as e,c as t,o as n,a3 as a}from"./chunks/framework.DA38T8Xw.js";const p=JSON.parse('{"title":"VL6: Metadaten","description":"","frontmatter":{},"headers":[],"relativePath":"lz/VL_06_Metadaten_LZ.md","filePath":"lz/VL_06_Metadaten_LZ.md"}'),r={name:"lz/VL_06_Metadaten_LZ.md"},i=a('<h1 id="vl6-metadaten" tabindex="-1">VL6: Metadaten <a class="header-anchor" href="#vl6-metadaten" aria-label="Permalink to &quot;VL6: Metadaten&quot;">​</a></h1><p><strong>Ausgangslage</strong></p><ul><li><p>Die S. arbeiten mit Metadaten ohne ein konkretes Verständnis davon zu haben.</p></li><li><p>Den S. ist die Bedeutung von Metadaten für das FDM nicht bewusst.</p></li></ul><h2 id="lernziele-fur-den-online-kurs" tabindex="-1">Lernziele für den Online-Kurs <a class="header-anchor" href="#lernziele-fur-den-online-kurs" aria-label="Permalink to &quot;Lernziele für den Online-Kurs&quot;">​</a></h2><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">1</td><td style="text-align:left;">Die S. können Metadaten bezeichnen (2) und erläutern (2), welche Funktionen Metadaten (insbesondere für die Nachnutzung von Daten) im FDM/Forschungsprozess haben.</td><td style="text-align:left;">Die S. setzen sich mit Metadaten sowie der Einordnung und dem Verständnis der Relevanz von Metadaten im FDM/Forschungsprozess auseinander.</td></tr><tr><td style="text-align:left;">2</td><td style="text-align:left;">Die S. sind in der Lage, anhand von Beispielszenarien Metadaten zu erkennen und diese anzugeben (1).</td><td style="text-align:left;">Die S. können Metadaten in der (Forschungs- )Praxis erkennen.</td></tr><tr><td style="text-align:left;">Lernziele für den Unterricht in Präsenz</td><td style="text-align:left;"></td><td style="text-align:left;"></td></tr><tr><td style="text-align:left;">3</td><td style="text-align:left;">Die S. sind in der Lage anhand eines Beispielprojekts sinnvolle Metadaten abzuleiten (5) und ihre Entscheidung zu begründen (6).</td><td style="text-align:left;">Die S. können Metadaten selber erstellen und ihr erworbenes Wissen künftig in Studienprojekten umsetzen.</td></tr></tbody></table><h2 id="detaillierte-kursplanung-1" tabindex="-1">Detaillierte Kursplanung[^1] <a class="header-anchor" href="#detaillierte-kursplanung-1" aria-label="Permalink to &quot;Detaillierte Kursplanung[^1]&quot;">​</a></h2><p><strong>Online-Kurs</strong></p><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden / Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 30 min.</td><td>Einführung in das Thema:<br>Infotext zu Metadaten inkl. Grafiken:<br><ul><li>Was sind Metadaten?</li><li>Zweck</li><li>Beispiele</li><li>Arten von Metadaten</li><li>Metadatenstandards</li></ul><br>Video von Mirjam Blümm / oder neu erstelltes Video<br>Quiz zu „Arten von Metadaten”</td><td>Die S. können Metadaten bezeichnen(2) und erläutern(2), welche Funktionen Metadaten (insbesondere für die Nachnutzung von Daten) im FDM/ Forschungsprozess haben.<br>&gt;Die S. sind in der Lage, anhand von Beispielszenarien Metadaten zu erkennen und diese anzugeben(1).</td><td>Text und Erklärvideo als Informationsquelle<br>Optionales Quiz</td><td>EA</td></tr><tr><td>Ca. 10 min.</td><td>Leitfragen zur Vorbereitung für Präsenzphase</td><td></td><td>Schriftliche Beantwortung der Leitfragen im Lernportfolio zur Vertiefung und Vorbereitung auf Präsenzphase (Transferaufgabe)</td><td>EA</td></tr></tbody></table><hr><p>[^1]:<strong>Legende der Sozialformen:</strong> PL = Plenum; L = Lehrende*r; S = Studierende; LV = Lehrvortrag; EA = Einzelarbeit, GA = Gruppenarbeit.</p><h3 id="prasenzphase" tabindex="-1">Präsenzphase <a class="header-anchor" href="#prasenzphase" aria-label="Permalink to &quot;Präsenzphase&quot;">​</a></h3><h4 id="einstiegsphase" tabindex="-1">Einstiegsphase <a class="header-anchor" href="#einstiegsphase" aria-label="Permalink to &quot;Einstiegsphase&quot;">​</a></h4><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden / Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 5 min.</td><td>Begrüßung u. Vorstellung der zeitlichen und thematischen Agenda</td><td>L. bietet Orientierung</td><td></td><td>LV</td></tr><tr><td>Ca. 20 min.</td><td>Plenumsdiskussion<br><br>„Was zeichnet eine möglichst schlechte Metadatenpraxis aus?“<br><br>alternativ<br><br>Scary Tales</td><td>Ergebnissicherung, Vertiefung der Inhalte aus der Online-PhaseDie S. werden aktiviert und können ihr vorhandenes Vorwissen einbringen und gleichzeitig assoziatives Denken üben.</td><td>Kopfstandmethode<br><br>Diskussion</td><td>PL</td></tr></tbody></table><h4 id="arbeitsphase" tabindex="-1">Arbeitsphase <a class="header-anchor" href="#arbeitsphase" aria-label="Permalink to &quot;Arbeitsphase&quot;">​</a></h4><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden / Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 30 min.</td><td>Gruppeneinteilung zur Praktischen Übung<br>Gruppenaufgabe: Jede Gruppe erhält ein Szenario + Hilfsmittel und soll Metadaten für die darin beschriebenen Daten identifizieren</td><td>Die S. besprechen und erarbeiten jeweils ein Fallbeispiel anhand von zur Verfügung gestellten Hilfsmitteln<br> Die S. sind in der Lage anhand eines Beispielprojekts sinnvolle Metadaten abzuleiten(5) und ihre Entscheidung zu begründen(6). Vertiefung und Festigung der Inhalte durch Transferaufgabe und Anwendungsbezug</td><td>Die S. werden in kleine Gruppen aufgeteilt.<br>Jede Gruppe erhält ein Szenario + Hilfsmittel und soll Metadaten für die darin beschriebenen Daten identifizieren.<br>Erarbeitung von Fallbeispielen in Gruppenarbeit<br>Vorbereitung von Impulsvorträgen</td><td>GA</td></tr><tr><td>Ca. 30 min.</td><td>Präsentation der einzelnen Gruppen mit jeweils anschließender Besprechung von Erkenntnissen / Beobachtungen</td><td>Ergebnissicherung</td><td>Impulsvorträge der Gruppen und Plenumsdiskussion</td><td>PL</td></tr><tr><td>Ca. 60 min.</td><td>Alternativ zu Gruppenaufgabe:<br>Metadaten spielerisch mit Lego vermitteln</td><td>Vertiefung und Festigung der Inhalte durch Transferaufgabe und Anwendungsbezug</td><td>Gruppenarbeit</td><td>GA</td></tr></tbody></table><h4 id="abschluss" tabindex="-1">Abschluss <a class="header-anchor" href="#abschluss" aria-label="Permalink to &quot;Abschluss&quot;">​</a></h4><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden / Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 5 min.</td><td>Offene Fragen / Unklarheiten klären Ausblick auf und Arbeitsauftrag für nächste Präsenzveranstaltung</td><td>L. erklärt Erwartungen für nächste Stunde.<br><br>L. bietet S. an, offene Fragen zum Vorlesungsthema, dem Seminarverlauf und/oder anderen Aspekten zu besprechen.</td><td></td><td>PL</td></tr></tbody></table><h3 id="prasenzphase-erlauterungen" tabindex="-1">Präsenzphase / Erläuterungen <a class="header-anchor" href="#prasenzphase-erlauterungen" aria-label="Permalink to &quot;Präsenzphase / Erläuterungen&quot;">​</a></h3><h4 id="einstiegsphase-1" tabindex="-1">Einstiegsphase <a class="header-anchor" href="#einstiegsphase-1" aria-label="Permalink to &quot;Einstiegsphase&quot;">​</a></h4><p>Begrüßung</p><h1 id="ca-20-min-diskussion" tabindex="-1">(ca. 20 Min.) Diskussion <a class="header-anchor" href="#ca-20-min-diskussion" aria-label="Permalink to &quot;(ca. 20 Min.) Diskussion&quot;">​</a></h1><p>Alternative 1:</p><p>Methode: Kopfstandmethode im Plenum „Was zeichnet eine möglichst schlechte Metadatenpraxis aus?“</p><p>Alternative 2:</p><p>Vorstellung und Lösung von zwei Scary Tales im Plenum</p><p>31 - Unbeschriebenes Blatt (<a href="https://forschungsdaten-thueringen.de/geschichten/articles/unbeschriebenes-blatt-de.html" target="_blank" rel="noreferrer">https://forschungsdaten-thueringen.de/geschichten/articles/unbeschriebenes-blatt-de.html</a>)</p><p>36 - Lost in Translation (<a href="https://forschungsdaten-thueringen.de/geschichten/articles/Lost-in-translation-de.html" target="_blank" rel="noreferrer">https://forschungsdaten-thueringen.de/geschichten/articles/Lost-in-translation-de.html</a>)</p><h4 id="arbeitsphase-1" tabindex="-1">Arbeitsphase <a class="header-anchor" href="#arbeitsphase-1" aria-label="Permalink to &quot;Arbeitsphase&quot;">​</a></h4><h1 id="praktische-ubung-szenarien" tabindex="-1">Praktische Übung (Szenarien) <a class="header-anchor" href="#praktische-ubung-szenarien" aria-label="Permalink to &quot;Praktische Übung (Szenarien)&quot;">​</a></h1><p>(ca. 30 Min.) L. teilt die S. in Gruppen ein und erläutert den Arbeitsauftrag.</p><p>Gruppenaufgabe: Jede Gruppe erhält ein Szenario + Hilfsmittel und soll Metadaten für die darin beschriebenen Daten identifizieren</p><p>(ca. 30 Min.) Präsentation der einzelnen Gruppe mit anschließender Besprechung von Erkenntnissen / Beobachtungen</p><p>alternativ:</p><p>Praktische Übung (Metadaten spielerisch mit Lego vermitteln, ca. 60 Min.)</p><h4 id="abschluss-1" tabindex="-1">Abschluss <a class="header-anchor" href="#abschluss-1" aria-label="Permalink to &quot;Abschluss&quot;">​</a></h4><p>L. fragt die S., ob noch Unklarheiten bestehen oder offene Fragen vorhanden sind</p><p>L. gibt Ausblick auf nächstes Thema und erinnert an die (online) Vorbereitung zur nächsten Präsenzveranstaltung</p><h2 id="mogliche-erganzende-materialien-im-lms" tabindex="-1">(Mögliche) Ergänzende Materialien im LMS <a class="header-anchor" href="#mogliche-erganzende-materialien-im-lms" aria-label="Permalink to &quot;(Mögliche) Ergänzende Materialien im LMS&quot;">​</a></h2><p>Video I:<a href="https://th-koeln.sciebo.de/s/IQkjPl7c7gxfzPk" target="_blank" rel="noreferrer">Metadatenformate</a> (mp4, 5:34,<a href="https://th-koeln.sciebo.de/s/IQkjPl7c7gxfzPk/download" target="_blank" rel="noreferrer">https://th-koeln.sciebo.de/s/IQkjPl7c7gxfzPk/download</a>)</p><p>Quiz zu Arten von Metadaten</p><h2 id="materialien-zur-praktischen-ubung-szenarien" tabindex="-1">Materialien zur Praktischen Übung (Szenarien) <a class="header-anchor" href="#materialien-zur-praktischen-ubung-szenarien" aria-label="Permalink to &quot;Materialien zur Praktischen Übung (Szenarien)&quot;">​</a></h2><h1 id="scary-tales" tabindex="-1">Scary Tales <a class="header-anchor" href="#scary-tales" aria-label="Permalink to &quot;Scary Tales&quot;">​</a></h1><p>31 - Unbeschriebenes Blatt (<a href="https://forschungsdaten-thueringen.de/geschichten/articles/unbeschriebenes-blatt-de.html" target="_blank" rel="noreferrer">https://forschungsdaten-thueringen.de/geschichten/articles/unbeschriebenes-blatt-de.html</a>)</p><p>36 - Lost in Translation (<a href="https://forschungsdaten-thueringen.de/geschichten/articles/Lost-in-translation-de.html" target="_blank" rel="noreferrer">https://forschungsdaten-thueringen.de/geschichten/articles/Lost-in-translation-de.html</a>)</p><h3 id="alternative-gruppenaufgabe" tabindex="-1">Alternative Gruppenaufgabe <a class="header-anchor" href="#alternative-gruppenaufgabe" aria-label="Permalink to &quot;Alternative Gruppenaufgabe&quot;">​</a></h3><h1 id="praktische-ubung" tabindex="-1">Praktische Übung <a class="header-anchor" href="#praktische-ubung" aria-label="Permalink to &quot;Praktische Übung&quot;">​</a></h1><p>Metadaten spielerisch mit Lego vermitteln (ca. 60 Min.)</p><p>Original:<a href="http://dx.doi.org/10.36399/gla.pubs.196477" target="_blank" rel="noreferrer">http://dx.doi.org/10.36399/gla.pubs.196477</a> (Donaldson, M. and Mahon, M.: LEGO® Metadata for Reproducibility game pack)</p><p>Deutsche Übersetzung:<a href="https://doi.org/10.5281/zenodo.3733163" target="_blank" rel="noreferrer">https://doi.org/10.5281/zenodo.3733163</a> Biernacka, Katarzyna (Übersetzerin), Donaldson, M. and Mahon, M.: LEGO® Metadaten für die Reproduzierbarkeit</p><h2 id="taxonomiestufen" tabindex="-1">Taxonomiestufen <a class="header-anchor" href="#taxonomiestufen" aria-label="Permalink to &quot;Taxonomiestufen&quot;">​</a></h2><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">Taxonomiestufe 1</td><td style="text-align:left;">Wissen, Kenntnisse (Tatsachen, Begriffe nennen)</td><td style="text-align:left;">Informationen möglichst wortgenau erinnern wiedergeben können, Informationen sinnerhaltend umformen und in eigenen Worten wiedergeben sowie zusammenfassen können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 2</td><td style="text-align:left;">Verständnis (erklären, begründen, mit anderen Worten umschreiben)</td><td style="text-align:left;"></td></tr><tr><td style="text-align:left;">Taxonomiestufe 3</td><td style="text-align:left;">Anwendung (Anwendung des Wissens, z.B. bei der Lösung eines praktischen Problems)</td><td style="text-align:left;">Abstraktionen (Regeln, Methoden etc.) in konkreten Situationen anwenden können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 4</td><td style="text-align:left;">Analyse (eine reale Situation in ihre Elemente zerlegen)</td><td style="text-align:left;">Ideen, Problemstellungen in ihre Elemente zerlegen und vergleichen können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 5</td><td style="text-align:left;">Synthese (aus verschiedenen Erlebnissen eine neue Situation konstruieren)</td><td style="text-align:left;">Einzelne Elemente zu einer Ganzheit formen</td></tr><tr><td style="text-align:left;">Taxonomiestufe 6</td><td style="text-align:left;">Bewertung (eine Situation beurteilen, überprüfen)</td><td style="text-align:left;">Ein bewertendes Urteil abgeben können</td></tr></tbody></table><h2 id="quellen" tabindex="-1">Quellen: <a class="header-anchor" href="#quellen" aria-label="Permalink to &quot;Quellen:&quot;">​</a></h2><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Lernziele Formulieren leicht gemacht</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/lernziele-formulieren-leicht-gemacht.htm" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/lernziele-formulieren-leicht-gemacht.htm</a></p><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Lernziele definieren</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/lernziele-definieren.html" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/lernziele-definieren.html</a></p><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Formulierungshilfen für Lernziele</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/formulierungshilfen-fur-lernziele.html" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/formulierungshilfen-fur-lernziele.html</a></p>',55),s=[i];function l(d,h,u,o,b,g){return n(),t("div",null,s)}const f=e(r,[["render",l]]);export{p as __pageData,f as default};
