import{_ as e,c as n,o as t,a3 as i}from"./chunks/framework.DA38T8Xw.js";const m=JSON.parse('{"title":"VL7: Ordnerstruktur und Dateibenennungen","description":"","frontmatter":{},"headers":[],"relativePath":"lz/VL_07_Dateisystem_LZ.md","filePath":"lz/VL_07_Dateisystem_LZ.md"}'),r={name:"lz/VL_07_Dateisystem_LZ.md"},a=i('<h1 id="vl7-ordnerstruktur-und-dateibenennungen" tabindex="-1">VL7: Ordnerstruktur und Dateibenennungen <a class="header-anchor" href="#vl7-ordnerstruktur-und-dateibenennungen" aria-label="Permalink to &quot;VL7: Ordnerstruktur und Dateibenennungen&quot;">​</a></h1><p><strong>Ausgangslage</strong></p><ul><li><p>Die Ablage von Daten erfolgt bei den S. aufgrund fehlender Sensibilisierung größtenteils unstrukturiert und ad hoc.</p></li><li><p>Es wird auf keine Nachnutzbarkeit (auch von Dritten) geachtet.</p></li></ul><h2 id="lernziele-fur-den-online-kurs-liascript" tabindex="-1">Lernziele für den Online-Kurs (LiaScript) <a class="header-anchor" href="#lernziele-fur-den-online-kurs-liascript" aria-label="Permalink to &quot;Lernziele für den Online-Kurs (LiaScript)&quot;">​</a></h2><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">1</td><td style="text-align:left;">Die S. können eine Dateibenennungskonvention und Merkmale einer effizienten Ordnerstruktur beschreiben(1).</td><td style="text-align:left;">Die S. haben verschiedene Konventionen für die Benennungen von Dateien kennengelernt und was eine effiziente Ordnerstruktur ausmacht.</td></tr><tr><td style="text-align:left;">2</td><td style="text-align:left;">Die S. können den Nutzen einer guten Ordnerstruktur und einer Dateibenennungskonvention im FDM darlegen(2).</td><td style="text-align:left;">Die S. erkennen, warum es in einem Forschungsprozess wichtig ist, Daten und Dateien strukturiert zu ordnen und sinnvoll zu benennen.</td></tr><tr><td style="text-align:left;">3</td><td style="text-align:left;">Die S. sind in der Lage, strukturierte und nachhaltige Strategien zur Datenablage anzuwenden(3).</td><td style="text-align:left;">Die S. wissen, worauf bei einer Datenablage (inkl. Ordnerstruktur und Dateibenennung) geachtet werden muss und können dieses Wissen anwenden.</td></tr></tbody></table><h2 id="lernziele-fur-den-unterricht-in-prasenz" tabindex="-1">Lernziele für den Unterricht in Präsenz <a class="header-anchor" href="#lernziele-fur-den-unterricht-in-prasenz" aria-label="Permalink to &quot;Lernziele für den Unterricht in Präsenz&quot;">​</a></h2><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">4</td><td style="text-align:left;">Die S. können begründen(6), welchen Mehrwert eine strukturierte und nachhaltige Datenablage und Dateibenennungskonvention bietet.</td><td style="text-align:left;">Die S. haben genau verstanden, wofür eine strukturierte und nachhaltige Datenablage sinnvoll ist und weshalb sie sich im eigenen Sinne und für andere daran halten sollten.</td></tr><tr><td style="text-align:left;">5</td><td style="text-align:left;">Die S. sind in der Lage, anhand von Beispielen geeignete (nachhaltige) und weniger geeignete Ordnerstrukturen sowie Dateibenennungskonventionen voneinander zu unterscheiden(4) und dies kritisch zu bewerten(6).</td><td style="text-align:left;">Die S. können unterschiedliche Ordnerstrukturen und Benennungsschemata miteinander vergleichen und kritisch beurteilen.</td></tr><tr><td style="text-align:left;">6</td><td style="text-align:left;">Die S. sind in der Lage für eigene Dateien (oder einen Beispieldatensatz) eine sinnvolle Benennungskonvention (inkl. Versionierungsmethoden) und eine effiziente Ordnerstruktur zu entwickeln (5).</td><td style="text-align:left;">Die S. können selbständig eine eigene sinnvolle Ordnerstruktur mit einem sinnvollen Benennungsschema entwickeln, sodass Daten und Dateien auch im Nachhinein/von anderen wiedergefunden werden können. Sie nehmen dieses Wissen mit in ihr Studium (in ihre Projekte) und wenden es dort an.</td></tr></tbody></table><h2 id="detaillierte-kursplanung-1" tabindex="-1">Detaillierte Kursplanung[^1] <a class="header-anchor" href="#detaillierte-kursplanung-1" aria-label="Permalink to &quot;Detaillierte Kursplanung[^1]&quot;">​</a></h2><p><strong>Online-Kurs</strong></p><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden/Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 15 min.</td><td>Einführung in das Thema Datenablage: Ordnerstruktur und Dateibenennungen. <br>Merkmale und Kriterien einer nachhaltigen Ordnerstruktur zur Speicherung von Daten und Dateien. <br>Wichtige Aspekte zu den Benennungen von Dateien (inkl. Versionierung etc.) hervorheben.</td><td>Die S. können eine Dateibenennungskonvention und Merkmale einer effizienten Ordnerstruktur beschreiben(1).</td><td>Infotexte zu den Inhalten, inkl. Grafiken. <br>Erklärvideo <br>Antwort zur ersten Leitfrage im Lernportfolio notieren (für eine ggf. Diskussion im Plenum).</td><td>EA</td></tr><tr><td>Ca. 10 min.</td><td>Die Relevanz einer strukturierten und nachhaltigen Datenablage (für sich und andere) im Forschungsprozess erläutern.</td><td>Die S. können den Nutzen einer guten Ordnerstruktur und einer Dateibenennungskonvention im FDM darlegen(2).</td><td>Antwort zur zweiten Leitfrage im Lernportfolio notieren (für eine ggf. Diskussion im Plenum).</td><td>EA</td></tr><tr><td>Ca. 5 min.</td><td>Die Merkmale und Kriterien einer strukturierten und nachhaltigen Datenablage in der Form von Beispielen erläutern.</td><td>Die S. sind in der Lage, strukturierte und nachhaltige Strategien zur Datenablage anzuwenden(3).</td><td>Optionales (Single Choice) Quiz: <br>Mögliche Beispiele zu einer Ordnerstruktur oder einer Dateibenennung werden angeboten und die S. müssen die korrekte Antwort auswählen.</td><td>EA</td></tr></tbody></table><hr><p>[^1]:<strong>Legende der Sozialformen:</strong> PL = Plenum; L = Lehrende*r; S = Studierende; LV = Lehrvortrag; EA = Einzelarbeit, GA = Gruppenarbeit.</p><h3 id="prasenzphase" tabindex="-1">Präsenzphase <a class="header-anchor" href="#prasenzphase" aria-label="Permalink to &quot;Präsenzphase&quot;">​</a></h3><h4 id="einstiegsphase" tabindex="-1">Einstiegsphase <a class="header-anchor" href="#einstiegsphase" aria-label="Permalink to &quot;Einstiegsphase&quot;">​</a></h4><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden/Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 5 min.</td><td>Begrüßung und Vorstellung der zeitlichen und thematischen Agenda</td><td>L. bietet Orientierung</td><td></td><td>LV</td></tr><tr><td>Ca. 15 min.</td><td>ScaryTale (unten ein Beispiel). <br>Zum Einstieg wird eine ScaryTale im Plenum „gespielt“.</td><td>Die S. können begründen(6), welchen Mehrwert eine strukturierte und nachhaltige Datenablage und Dateibenennungskonvention für sie selbst und für andere bietet.</td><td>L. führt die ScaryTale ein. Hier sollen die S. im Plenum raten und gemeinsam eine Antwort finden. <br>Nach Beantwortung der ScaryTale sollen die S. die Sinnhaftigkeit und Relevanz einer strukturierten und nachhaltigen Datenablage für sich selbst (im Studium) und für andere (z. B. bei Projektarbeiten im Studium) erläutern (siehe Leitfrage 3).</td><td>PL</td></tr></tbody></table><h4 id="arbeitsphase" tabindex="-1">Arbeitsphase <a class="header-anchor" href="#arbeitsphase" aria-label="Permalink to &quot;Arbeitsphase&quot;">​</a></h4><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden/Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 40 min.</td><td>Gruppenarbeit zum Thema Dateiablage. <br>Anhand einer Liste von Beispielen sollen die S. in Gruppen gute von schlechten Ordnerstrukturen und Dateibenennungskonventionen unterscheiden. <br>Anschließend kurze Besprechung/Auflösung im PL.</td><td>Die S. sind in der Lage, anhand von Beispielen geeignete (nachhaltige) und weniger geeignete Ordnerstrukturen sowie Dateibenennungskonventionen voneinander zu unterscheiden(4) und dies kritisch zu bewerten(6).</td><td>L. teilt die S. in Gruppen ein. <br>Die L. stellt den S. eine Liste von ca. drei Beispielen vor. Diese sind Beispiele von geeigneten (nachhaltigen) und weniger geeigneten Ordnerstrukturen und Dateibenennungskonventionen. <br>Die S. müssen dessen Sinnhaftigkeit und Nachhaltigkeit einordnen und bewerten. <br>Anschließend tragen die Gruppen ihre Bewertungen im PL vor und es erfolgt eine gemeinsame Reflexion und Diskussion.</td><td>GA / PL</td></tr><tr><td>Ca. 25 min.</td><td>Einzelarbeit <br>Eigene Datenablage sinnvoll überarbeiten. <br>ODER <br>In Zweiergruppen sollen die S. die Dateien des Anderen sinnvoller benennen. <br>ALTERNATIV <br>Beispiel-Datenablage sinnvoll überarbeiten.</td><td>Die S. sind in der Lage, einen eigenen Datensatz/für eigene Dateien (oder einen Beispieldatensatz) eine sinnvolle Benennungskonvention (inkl. Versionierungsmethoden) und eine effiziente Ordnerstruktur zu entwickeln(5).</td><td>Die S. können sich ihre eigenen Dateien (oder die eines Anderen) vornehmen und diese sinnvoll und nachhaltig benennen und in Ordner strukturieren. <br>ALTERNATIV <br>2. Die L. bietet ein Beispiel von einer chaotischen Dateibenennung und Struktur. Die S. müssen diese bearbeiten und die Dateien sinnvoll umbenennen und strukturieren. <br>Anschließend eine kurze abschließende Diskussion im PL.</td><td>EA / PL</td></tr></tbody></table><h4 id="abschluss" tabindex="-1">Abschluss <a class="header-anchor" href="#abschluss" aria-label="Permalink to &quot;Abschluss&quot;">​</a></h4><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden/Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 5 min.</td><td>Offene Fragen / Unklarheiten klären Ausblick auf und Arbeitsauftrag für nächste Präsenzveranstaltung</td><td>L. erklärt die Erwartungen für nächste Stunde. <br>L. bietet S. an, offene Fragen zum Vorlesungsthema, dem Seminarverlauf und/oder anderen Aspekten zu besprechen.</td><td></td><td>PL</td></tr></tbody></table><h2 id="prasenzphase-1" tabindex="-1">Präsenzphase <a class="header-anchor" href="#prasenzphase-1" aria-label="Permalink to &quot;Präsenzphase&quot;">​</a></h2><h3 id="einstiegsphase-1" tabindex="-1">Einstiegsphase <a class="header-anchor" href="#einstiegsphase-1" aria-label="Permalink to &quot;Einstiegsphase&quot;">​</a></h3><p>Begrüßung</p><h1 id="ca-15-min-methode-diskussion-scarytale" tabindex="-1">(ca. 15 Min.) Methode: Diskussion - ScaryTale <a class="header-anchor" href="#ca-15-min-methode-diskussion-scarytale" aria-label="Permalink to &quot;(ca. 15 Min.) Methode: Diskussion - ScaryTale&quot;">​</a></h1><p>Aufgabe: Raten Sie und finden Sie gemeinsam eine Lösung für das ScaryTale.</p><ul><li>Wo liegen die Fehler?</li><li>Was ist falsch gelaufen?</li><li>Wie kann es revidiert werden?</li><li>Was hätte korrekterweise getan werden müssen?</li></ul><p>Beantwortung von Leitfrage 3: Erläuterung der Sinnhaftigkeit und Relevanz einer strukturierten und nachhaltigen Datenablage für die S. selbst (im Studium) und für andere (z. B. bei Projektarbeiten im Studium).</p><h3 id="arbeitsphase-1" tabindex="-1">Arbeitsphase <a class="header-anchor" href="#arbeitsphase-1" aria-label="Permalink to &quot;Arbeitsphase&quot;">​</a></h3><h1 id="ca-40-min-gruppenarbeit" tabindex="-1">(ca. 40 Min.) Gruppenarbeit <a class="header-anchor" href="#ca-40-min-gruppenarbeit" aria-label="Permalink to &quot;(ca. 40 Min.) Gruppenarbeit&quot;">​</a></h1><p>L. teilt die S. in Gruppen auf und stellt den S. die Liste von ca. drei Beispielen vor. Die Gruppen setzen sich zusammen und bearbeiten die Aufgabe.</p><p>Die S. müssen die Sinnhaftigkeit und Nachhaltigkeit der Datenablage (Ordnerstruktur und Dateibenennungskonventionen) der Beispiele einordnen und bewerten.</p><p>Nach der Gruppenarbeit bringt L. Alle Gruppen zusammen. Jede Gruppe trägt ihre Bewertung mit Anregung zur Reflexion und Diskussion vor.</p><h1 id="ca-25-min-einzelarbeit-zwei-moglichkeiten" tabindex="-1">(ca. 25 Min.) Einzelarbeit, zwei Möglichkeiten <a class="header-anchor" href="#ca-25-min-einzelarbeit-zwei-moglichkeiten" aria-label="Permalink to &quot;(ca. 25 Min.) Einzelarbeit, zwei Möglichkeiten&quot;">​</a></h1><ol><li>Eigene Datenablage sinnvoll überarbeiten</li></ol><p>Die S. können sich jeweils ihre eigenen Dateien vornehmen* und diese sinnvoll und nachhaltig benennen und in Ordner strukturieren.</p><p>* die S. können sich frei entscheiden, welche ihrer Daten und Dateien sie gerne umstrukturieren und umbenennen (und die Benennungskonvention in einer separaten Textdatei festhalten) möchten. Dies können Dateien aus einem bestimmten (Studiums)Projekt, aus einem bestimmten Semester oder auch Privatdateien (z. B. Rechnungen etc.) sein, dessen Ordnerstruktur und Dateibenennungen sie gerne sinnvoll überarbeiten möchten.</p><p>ODER</p><p>Die S. können in Zweiergruppen die jeweils gegenseitigen Dateien sinnvoll strukturieren und benennen.</p><ol start="2"><li>Beispiel-Datenablage sinnvoll überarbeiten</li></ol><p>Die L. bietet ein Beispiel von einer chaotischen Dateibenennung und Struktur. Die S. müssen dieses bearbeiten, indem sie die Dateien sinnvoll umbenennen (und die Benennungskonvention in einer separaten Textdatei festhalten) und umstrukturieren.</p><p>Anschließend holt L. alle S. zurück ins PL und leitet eine kurze abschließende Diskussion ein.</p><h3 id="abschluss-1" tabindex="-1">Abschluss <a class="header-anchor" href="#abschluss-1" aria-label="Permalink to &quot;Abschluss&quot;">​</a></h3><p>L. fragt S., ob noch Unklarheiten bestehen oder offene Fragen vorhanden sind</p><p>L. gibt Ausblick auf nächstes Thema und erinnert an die (online) Vorbereitung zur nächsten Präsenzveranstaltung</p><h2 id="taxonomiestufen" tabindex="-1">Taxonomiestufen <a class="header-anchor" href="#taxonomiestufen" aria-label="Permalink to &quot;Taxonomiestufen&quot;">​</a></h2><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">Taxonomiestufe 1</td><td style="text-align:left;">Wissen, Kenntnisse (Tatsachen, Begriffe nennen)</td><td style="text-align:left;">Informationen möglichst wortgenau erinnern wiedergeben können, Informationen sinnerhaltend umformen und in eigenen Worten wiedergeben sowie zusammenfassen können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 2</td><td style="text-align:left;">Verständnis (erklären, begründen, mit anderen Worten umschreiben)</td><td style="text-align:left;"></td></tr><tr><td style="text-align:left;">Taxonomiestufe 3</td><td style="text-align:left;">Anwendung (Anwendung des Wissens, z.B. bei der Lösung eines praktischen Problems)</td><td style="text-align:left;">Abstraktionen (Regeln, Methoden etc.) in konkreten Situationen anwenden können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 4</td><td style="text-align:left;">Analyse (eine reale Situation in ihre Elemente zerlegen)</td><td style="text-align:left;">Ideen, Problemstellungen in ihre Elemente zerlegen und vergleichen können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 5</td><td style="text-align:left;">Synthese (aus verschiedenen Erlebnissen eine neue Situation konstruieren)</td><td style="text-align:left;">Einzelne Elemente zu einer Ganzheit formen</td></tr><tr><td style="text-align:left;">Taxonomiestufe 6</td><td style="text-align:left;">Bewertung (eine Situation beurteilen, überprüfen)</td><td style="text-align:left;">Ein bewertendes Urteil abgeben können</td></tr></tbody></table><h2 id="quellen" tabindex="-1">Quellen: <a class="header-anchor" href="#quellen" aria-label="Permalink to &quot;Quellen:&quot;">​</a></h2><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Lernziele Formulieren leicht gemacht</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/lernziele-formulieren-leicht-gemacht.htm" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/lernziele-formulieren-leicht-gemacht.htm</a></p><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Lernziele definieren</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/lernziele-definieren.html" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/lernziele-definieren.html</a></p><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Formulierungshilfen für Lernziele</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/formulierungshilfen-fur-lernziele.html" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/formulierungshilfen-fur-lernziele.html</a></p>',49),s=[a];function l(d,u,h,o,g,b){return t(),n("div",null,s)}const f=e(r,[["render",l]]);export{m as __pageData,f as default};
