import{_ as e,c as n,o as t,a3 as i}from"./chunks/framework.8CaK9a7u.js";const g=JSON.parse('{"title":"VL9: Daten finden und nachnutzen","description":"","frontmatter":{},"headers":[],"relativePath":"lz/VL_09_Finden_LZ.md","filePath":"lz/VL_09_Finden_LZ.md"}'),r={name:"lz/VL_09_Finden_LZ.md"},a=i('<h1 id="vl9-daten-finden-und-nachnutzen" tabindex="-1">VL9: Daten finden und nachnutzen <a class="header-anchor" href="#vl9-daten-finden-und-nachnutzen" aria-label="Permalink to &quot;VL9: Daten finden und nachnutzen&quot;">​</a></h1><p><strong>Ausgangslage</strong></p><p>Die S. wissen nicht,</p><ul><li>wo sie nach Daten (zum Nachnutzen) suchen sollen,</li><li>was Repositorien oder Datenportale sind,</li><li>worauf sie beim Suchen von Daten (zum Nachnutzen) achten sollen (z. B. Formate, Lizenzen),</li><li>wie sie gefundene Daten extrahieren können,</li><li>ob gefundene Daten für das eigene Projekt/Vorhaben sinnvoll nachgenutzt werden können oder,</li><li>ob eigene Daten erhoben werden sollen.</li></ul><h2 id="lernziele-fur-den-online-kurs" tabindex="-1">Lernziele für den Online-Kurs <a class="header-anchor" href="#lernziele-fur-den-online-kurs" aria-label="Permalink to &quot;Lernziele für den Online-Kurs&quot;">​</a></h2><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">1</td><td style="text-align:left;">Die S. können erläutern(2), was ein Repositorium ist und können verschiedene Arten und dessen Qualitätsmerkmale benennen(1).</td><td style="text-align:left;">Die S. verstehen, was Repositorien sind und worauf sie der der Benutzung achten müssen.</td></tr><tr><td style="text-align:left;">2</td><td style="text-align:left;">Die S. können aufführen(1), wo sie nach (welchen) Datensätzen suchen können.</td><td style="text-align:left;">Die S. wissen, wo sie nach welchen Datensätzen suchen können.</td></tr><tr><td style="text-align:left;">3</td><td style="text-align:left;">Die S. können benennen(1), auf welche Aspekte sie bei der Datennachnutzung achten müssen (zugänglich, zitierfähig, erschlossen usw.).</td><td style="text-align:left;">Die S. wissen, worauf sie achten müssen, um einen Datensatz zur Nachnutzung zu verwenden.</td></tr><tr><td style="text-align:left;">4</td><td style="text-align:left;">Die S. können beschreiben(1), was Persistente Identifikatoren (PID) sind und deren Nutzen und Funktionen erläutern (2) sowie Beispiele für verschiedene PIDs benennen(1).</td><td style="text-align:left;">Die S. lernen Beispiele und den Nutzen von PIDs kennen.</td></tr></tbody></table><h2 id="lernziele-fur-den-unterricht-in-prasenz" tabindex="-1">Lernziele für den Unterricht in Präsenz <a class="header-anchor" href="#lernziele-fur-den-unterricht-in-prasenz" aria-label="Permalink to &quot;Lernziele für den Unterricht in Präsenz&quot;">​</a></h2><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">5</td><td style="text-align:left;">Die S. können Vor- und Nachteile verschiedener Repositorien sowie der Nachnutzung von Daten gegenüberstellen(5).</td><td style="text-align:left;">Die S. können ihr erlerntes Wissen umsetzen, um Vor- und Nachteile von Repositorien bei unterschiedlichen Fallbeispielen zu erkennen.</td></tr><tr><td style="text-align:left;">6</td><td style="text-align:left;">Die S. können die Creative-Commons-Lizenzen unterscheiden(4).</td><td style="text-align:left;">Die S. verstehen, welche CC-Lizenzen wofür verwendet werden, was sie bedeuten, welche Daten sie in welcher Form nachnutzen dürfen und wie sie ihre Datensätze zitieren können. Dieses Wissen können sie in ihrem Studium anwenden.</td></tr><tr><td style="text-align:left;">7</td><td style="text-align:left;">Die S. sind in der Lage anhand eines Beispielszenarios, einen nachnutzbaren Datensatz aus einem Repositorium auszuwählen(6) und diesen auf Charakteristiken (wie PIDs, Lizenzen, Metadaten etc.) zu untersuchen (4).</td><td style="text-align:left;">Die S. können ihr gewonnenes Wissen in der Praxis umsetzen und auch zukünftig in ihrem Studium anwenden. Sie können durch Repositorien navigieren, auf relevante Merkmale achten, und nachnutzbare Datensätze zur Weiterverarbeitung/Analyse herunterladen.</td></tr><tr><td style="text-align:left;">8</td><td style="text-align:left;">Die S. sind in der Lage, relevante Informationen (z. B. Metadaten) eines nachnutzbaren Datensatzes abzuleiten(5), um diesen unter Anleitung korrekt zu zitieren.</td><td style="text-align:left;">Die S. wissen, auf welche Merkmale eines Datensatzes sie achten müssen, um diesen entsprechend zu zitieren.</td></tr></tbody></table><h2 id="detaillierte-kursplanung-1" tabindex="-1">Detaillierte Kursplanung[^1] <a class="header-anchor" href="#detaillierte-kursplanung-1" aria-label="Permalink to &quot;Detaillierte Kursplanung[^1]&quot;">​</a></h2><p><strong>Online-Kurs</strong></p><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden / Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 15 min.</td><td>Einführung zum Thema Daten finden. <br>Form und Funktion von Metasuchmaschinen, Datenportalen und Repositorien definieren. <br>Qualitätsmerkmale von (guten) Repositorien definieren und hervorheben, worauf geachtet werden muss.</td><td>Die S. können erläutern(2), was ein Repositorium ist und können verschiedene Arten und dessen Qualitätsmerkmale benennen(1).</td><td>Infotexte zu den Inhalten, inkl. Grafiken. <br>Screencasts, in denen Merkmale und mehrere Portale kurz vorgestellt werden. Und generisches Einführungsvideo von einer Datensuche. <br>S. notieren sich Antworten zur ersten Leitfrage im Lernportfolio (für eine ggf. Diskussion im Plenum).</td><td>EA</td></tr><tr><td>Ca. 15 min.</td><td>Einführung zum Thema Daten nachnutzen. <br>Detaillierte Definition von Kriterien, die bei der Datennachnutzung zu beachten sind. <br>Erläuterung des Themas „Zitieren“. <br>Definition von Aspekten, die für eine Zitation von Datensätzen bei der Nachnutzung beachtet werden müssen. <br>Die CC-Lizenzen werden hier vorgestellt.</td><td>Die S. können aufführen(1), wo sie nach (welchen) Datensätzen suchen können. <br>Die S. können benennen(1), auf welche Aspekte sie bei der Datennachnutzung achten müssen (zugänglich, zitierfähig, erschlossen usw.).</td><td>Infotexte zu den Inhalten, inkl. Grafiken. <br>Antwort zur zweiten Leitfrage im Lernportfolio notieren (für eine ggf. Diskussion im Plenum).</td><td>EA</td></tr><tr><td>Ca. 5 min.</td><td>PID definieren und deren Merkmale hervorheben. <br>Erläuterung der Relevanz von PIDs im Forschungsprozess.</td><td>Die S. können beschreiben(1), was PIDs sind und deren Nutzen und Funktionen erläutern(2) sowie Beispiele für verschiedene PIDs benennen(1).</td><td>Infotexte zu den Inhalten, inkl. Grafiken, auch mit Beispielen (Screenshots). <br>Beispiele von PIDs (von Datensätzen/Forschenden) zeigen. <br>Oder <br>Optional: Die S. bearbeiten eine Single-Choice-Frage für die Beschreibung eines PIDs <br>ALTERNATIV <br>Anhand von Bildern testen: ein Bild inkl. PID wird gezeigt, und dieses müssen die S. als Fill-in-the-Blanks eintragen.</td><td>EA</td></tr></tbody></table><hr><p>[^1]:<strong>Legende der Sozialformen:</strong> PL = Plenum; L = Lehrende*r; S = Studierende; LV = Lehrvortrag; EA = Einzelarbeit, GA = Gruppenarbeit.</p><h3 id="prasenzphase" tabindex="-1">Präsenzphase <a class="header-anchor" href="#prasenzphase" aria-label="Permalink to &quot;Präsenzphase&quot;">​</a></h3><h4 id="einstiegsphase" tabindex="-1">Einstiegsphase <a class="header-anchor" href="#einstiegsphase" aria-label="Permalink to &quot;Einstiegsphase&quot;">​</a></h4><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden/ Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 5 min.</td><td>Begrüßung und Vorstellung der zeitlichen und thematischen Agenda</td><td>L. bietet Orientierung</td><td></td><td>LV</td></tr><tr><td>Ca. 15 min.</td><td>Diskussionsrunde <br>Idealerweise auch eine Reflexion der S. zum Thema, ob Daten in einem Repositorium suchen und finden in ihrem Studium eine Rolle spielen (spielen könnte).</td><td>Die S. können Vor- und Nachteile verschiedener Repositorien sowie der Nachnutzung von Daten gegenüberstellen (5).</td><td>L. eröffnet eine Diskussion im PL der zwei beantworteten Leitfragen im Lernportfolio. <br>L. kann sich auf die weiterführenden Fragen zur Leitung der Diskussion unten in der Liste berufen.</td><td>PL</td></tr></tbody></table><h4 id="arbeitsphase" tabindex="-1">Arbeitsphase <a class="header-anchor" href="#arbeitsphase" aria-label="Permalink to &quot;Arbeitsphase&quot;">​</a></h4><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden/ Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 15 min.</td><td>Einzelarbeit zum Thema CC-Lizenzen: <br>BY <br>BY SA <br>BY SA ND <br>BY NC <br>BY NC SA <br>BY NC ND</td><td>Die S. können die Creative-Commons-Lizenzen unterscheiden(4).</td><td>Einzelarbeit: <br>L. bereitet vorab die Aufgabe mit Angabe der sechs CC-Lizenzen vor. <br>Die S. tragen individuell Definitionen, Kriterien und Beispiele für diese CC-Lizenzen ein. <br>Anschließend wird dies im Plenum diskutiert.</td><td>EA / PL</td></tr><tr><td>Ca. 50 min.</td><td>Gruppenarbeit. <br>Die S. müssen anhand eines Beispielthemas einen Datensatz finden und bestimmte Kriterien dieses Datensatzes hervorheben. <br>Außerdem müssen sie in ihren Gruppen, und anschließend im PL, drei Leitfragen beantworten (unten in der Liste notiert).</td><td>Die S. sind in der Lage anhand eines Beispielszenarios, einen nachnutzbaren Datensatz aus einem Repositorium auszuwählen(6), <br>diesen auf Charakteristiken (wie PIDs, Lizenzen, Metadaten etc.) zu untersuchen(4) und diesen unter Anleitung korrekt zu zitieren.</td><td>Gruppenarbeit. <br>Die L. teilt die S. in Gruppen ein. Das Thema und ein Beispielprojekt (Feld oder Datensatztyp) wird von der L. vorgegeben. <br>Die Gruppen können die Aufgabe nur erledigen, indem sie zusammenarbeiten <br>Danach leitet die L. eine offene Diskussion ein und bittet die Gruppen, ihre Arbeit vorzustellen. <br>Die S. überlegen sich Antworten zu folgenden Fragen:<br><ul><li>welchen Datensatz sie wo, weshalb und wie gefunden haben,</li><li>auf welche Aspekte sie geachtet haben.</li><li>Die Zitation des Datensatzes angeben.</li><li>Die drei Leitfragen im PL beantworten.</li></ul></td><td>GA / PL</td></tr></tbody></table><h4 id="abschluss" tabindex="-1">Abschluss <a class="header-anchor" href="#abschluss" aria-label="Permalink to &quot;Abschluss&quot;">​</a></h4><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden/ Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 5. min.</td><td>Offene Fragen / Unklarheiten klärenAusblick auf und Arbeitsauftrag für nächste Präsenzveranstaltung</td><td>L. erklärt Erwartungen für nächste Stunde. <br>L. bietet S. an, offene Fragen zum Vorlesungsthema, dem Seminarverlauf und/oder anderen Aspekten zu besprechen.</td><td></td><td>PL</td></tr></tbody></table><h2 id="prasenzphase-erlauterungen" tabindex="-1">Präsenzphase / Erläuterungen <a class="header-anchor" href="#prasenzphase-erlauterungen" aria-label="Permalink to &quot;Präsenzphase / Erläuterungen&quot;">​</a></h2><h3 id="einstiegsphase-1" tabindex="-1">Einstiegsphase <a class="header-anchor" href="#einstiegsphase-1" aria-label="Permalink to &quot;Einstiegsphase&quot;">​</a></h3><p>Begrüßung</p><p>(ca. 15 Min.) L. leitet eine Diskussionsrunde basierend auf den zwei im Lernportfolio beantworteten Leitfragen ein.</p><p>Mögliche weiterführende Fragen:</p><ul><li>Was sind Repositorien?</li><li>Wo kann man welche Datensätze finden?</li><li>Worauf muss ich achten, wenn ich Datensätze nachnutzen möchte? (3-5 Punkte besprechen)</li><li>Denken die S., dass Repositorien ihnen im Studium hilfreich sein könnten?</li><li>Könnten sich die S. vorstellen, Repositorien außerhalb der LV zu verwenden?</li></ul><h3 id="arbeitsphase-1" tabindex="-1">Arbeitsphase <a class="header-anchor" href="#arbeitsphase-1" aria-label="Permalink to &quot;Arbeitsphase&quot;">​</a></h3><p>(ca. 15 Min.) Einzelarbeit zum Thema CC-Lizenzen.</p><ul><li>L. erstellt vorab eine Übersicht zum Thema CC-Lizenzen. Hier müssen die S. die jeweiligen Definitionen, Kriterien und Beispiele pro Lizenz eintragen.</li><li>Als Hilfsmittel können die S. die Informationen zum Thema auf LiaScript nutzen.</li></ul><h1 id="ca-50-min-gruppenarbeit" tabindex="-1">(ca. 50 Min.) Gruppenarbeit <a class="header-anchor" href="#ca-50-min-gruppenarbeit" aria-label="Permalink to &quot;(ca. 50 Min.) Gruppenarbeit&quot;">​</a></h1><p>L. teilt die S. in Gruppen ein und stellt die Aufgabe und ein Beispielthema vor.</p><p>Die Gruppen setzen sich zusammen und bearbeiten die Aufgabe. Dabei achten sie auf folgendes:</p><ul><li>welchen Datensatz sie wo, weshalb und wie gefunden haben,</li><li>auf welche Aspekte sie geachtet haben.</li><li>Sie zitieren den Datensatz korrekterweise.</li></ul><p>Nach der Gruppenarbeit bittet L. jede Gruppe ihre Arbeit und die Ergebnisse vorzutragen. Dabei muss jede Gruppe die oben definierten Aspekte beantworten sowie die drei Leitfragen im PL beantworten.</p><h3 id="abschluss-1" tabindex="-1">Abschluss <a class="header-anchor" href="#abschluss-1" aria-label="Permalink to &quot;Abschluss&quot;">​</a></h3><p>L. fragt die S., ob noch Unklarheiten bestehen oder offene Fragen vorhanden sind</p><p>L. gibt Ausblick auf nächstes Thema und erinnert an die (online) Vorbereitung zur nächsten Präsenzveranstaltung</p><h2 id="taxonomiestufen" tabindex="-1">Taxonomiestufen <a class="header-anchor" href="#taxonomiestufen" aria-label="Permalink to &quot;Taxonomiestufen&quot;">​</a></h2><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">Taxonomiestufe 1</td><td style="text-align:left;">Wissen, Kenntnisse (Tatsachen, Begriffe nennen)</td><td style="text-align:left;">Informationen möglichst wortgenau erinnern wiedergeben können, Informationen sinnerhaltend umformen und in eigenen Worten wiedergeben sowie zusammenfassen können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 2</td><td style="text-align:left;">Verständnis (erklären, begründen, mit anderen Worten umschreiben)</td><td style="text-align:left;"></td></tr><tr><td style="text-align:left;">Taxonomiestufe 3</td><td style="text-align:left;">Anwendung (Anwendung des Wissens, z.B. bei der Lösung eines praktischen Problems)</td><td style="text-align:left;">Abstraktionen (Regeln, Methoden etc.) in konkreten Situationen anwenden können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 4</td><td style="text-align:left;">Analyse (eine reale Situation in ihre Elemente zerlegen)</td><td style="text-align:left;">Ideen, Problemstellungen in ihre Elemente zerlegen und vergleichen können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 5</td><td style="text-align:left;">Synthese (aus verschiedenen Erlebnissen eine neue Situation konstruieren)</td><td style="text-align:left;">Einzelne Elemente zu einer Ganzheit formen</td></tr><tr><td style="text-align:left;">Taxonomiestufe 6</td><td style="text-align:left;">Bewertung (eine Situation beurteilen, überprüfen)</td><td style="text-align:left;">Ein bewertendes Urteil abgeben können</td></tr></tbody></table><h2 id="quellen" tabindex="-1">Quellen: <a class="header-anchor" href="#quellen" aria-label="Permalink to &quot;Quellen:&quot;">​</a></h2><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Lernziele Formulieren leicht gemacht</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/lernziele-formulieren-leicht-gemacht.htm" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/lernziele-formulieren-leicht-gemacht.htm</a></p><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Lernziele definieren</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/lernziele-definieren.html" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/lernziele-definieren.html</a></p><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Formulierungshilfen für Lernziele</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/formulierungshilfen-fur-lernziele.html" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/formulierungshilfen-fur-lernziele.html</a></p>',43),s=[a];function l(d,h,u,o,b,f){return t(),n("div",null,s)}const m=e(r,[["render",l]]);export{g as __pageData,m as default};
