import{_ as e,c as t,o as n,a3 as i}from"./chunks/framework.8CaK9a7u.js";const m=JSON.parse('{"title":"VL10: Rechtliche Aspekte","description":"","frontmatter":{},"headers":[],"relativePath":"lz/VL_10_Recht_LZ.md","filePath":"lz/VL_10_Recht_LZ.md"}'),r={name:"lz/VL_10_Recht_LZ.md"},s=i('<h1 id="vl10-rechtliche-aspekte" tabindex="-1">VL10: Rechtliche Aspekte <a class="header-anchor" href="#vl10-rechtliche-aspekte" aria-label="Permalink to &quot;VL10: Rechtliche Aspekte&quot;">​</a></h1><p><strong>Ausgangslage</strong></p><ul><li>Die S. haben sich noch nicht mit rechtlichen Aspekten in Bezug zum FDM (beim Umgang mit Daten) auseinandergesetzt.</li></ul><h3 id="lernziele-fur-den-online-kurs" tabindex="-1">Lernziele für den Online-Kurs <a class="header-anchor" href="#lernziele-fur-den-online-kurs" aria-label="Permalink to &quot;Lernziele für den Online-Kurs&quot;">​</a></h3><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">LZ[^1] 1</td><td style="text-align:left;">Die S. können die wichtigsten Aspekte des Datenschutzes (inkl. der Rechtsgrundlagen zum Schutz personenbezogener Daten) im FDM beschreiben(2).</td><td style="text-align:left;">Die S. verstehen, auf welche rechtlichen Aspekte sie im Forschungsprozess achten müssen und weshalb das so wichtig ist.</td></tr><tr><td style="text-align:left;">LZ 2</td><td style="text-align:left;">Die S. können die Rolle von Urheberrecht und Lizenzrecht im FDM erläutern(2).</td><td style="text-align:left;">Die S. lernen die rechtlichen Hintergründe des Urheberrechts und Lizenzrechts kennen und wissen, welche Aspekte diesbezüglich zu berücksichtigen sind.</td></tr></tbody></table><p>[^1]: LZ = Lernziel</p><h3 id="lernziele-fur-den-unterricht-in-prasenz" tabindex="-1">Lernziele für den Unterricht in Präsenz <a class="header-anchor" href="#lernziele-fur-den-unterricht-in-prasenz" aria-label="Permalink to &quot;Lernziele für den Unterricht in Präsenz&quot;">​</a></h3><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">LZ 3</td><td style="text-align:left;">Die S. können herausstellen (4), was personenbezogene und sensible Daten sind, und entsprechende Beispiele einordnen (4).</td><td style="text-align:left;">Die S. wissen, was personenbezogene und sensible Daten sind und worauf bei der Erhebung derartiger Daten zu achten ist.</td></tr><tr><td style="text-align:left;">LZ 4</td><td style="text-align:left;">Die S. können kritisch beurteilen (6), auf welche rechtlichen Aspekte im FDM (z. B. bei der Erhebung von Daten) geachtet werden sollte.</td><td style="text-align:left;">Die S. verstehen, auf welche rechtlichen Aspekte sie im Forschungsprozess (insbesondere bei der Datenerhebung/-veröffentlichung) achten müssen und weshalb das so wichtig ist.</td></tr><tr><td style="text-align:left;">LZ 5</td><td style="text-align:left;">Die S. sind in der Lage, die wichtigsten rechtlichen Aspekte im Umgang mit Daten für ein Beispiel einzuordnen (4).</td><td style="text-align:left;">Die S. können sich beim Umgang mit Daten (in ihrem Studium) auf rechtliche Richtlinien beziehen (5) und diese beachten. Die S. fühlen sich mit den Konzepten von rechtlichen Richtlinien sicher, sodass sie sich beim Umgang mit Daten (z. B. für Projektarbeiten / Abschlussarbeiten In ihrem Studium) an diese Richtlinien halten können.</td></tr></tbody></table><h2 id="detaillierte-kursplanung-2" tabindex="-1">Detaillierte Kursplanung[^2] <a class="header-anchor" href="#detaillierte-kursplanung-2" aria-label="Permalink to &quot;Detaillierte Kursplanung[^2]&quot;">​</a></h2><p><strong>Online-Kurs</strong></p><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden / Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 15 min.</td><td>Einführung zum Thema rechtliche Aspekte und Rechtsgrundlagen im FDM. <br> Erläuterungen zum Thema Datenschutz und personenbezogene Daten. <br> Definition von Aspekten, die im FDM beachtet werden müssen und rechtlich bindend sind (insbesondere bei der Datenerhebung).</td><td>Die S. können die wichtigsten Aspekte des Datenschutzes (inkl. der Rechtsgrundlagen zum Schutz personenbezogener Daten) im FDM beschreiben (2).</td><td>Die S. müssen das LZ im Lernportfolio beantworten (für eine ggf. Diskussion im Plenum).</td><td>EA</td></tr><tr><td>Ca. 15 min.</td><td>Einführung zum Thema Urheberrecht von Forschungsdaten. <br> Erläuterung worauf, insbesondere bei der Nachnutzung von Daten, im Forschungsprozess geachtet werden muss. <br>Hervorhebung von relevanten Rechtsaspekten unterschiedlicher Lizenzen.</td><td>Die S. können die Rolle von Urheberrecht und Lizenzrecht im FDM erläutern (2).</td><td>Die S. müssen die Leitfragen im Lernportfolio beantworten (für eine ggf. Diskussion im Plenum).</td><td>EA</td></tr></tbody></table><hr><p>[^2]:<strong>Legende der Sozialformen:</strong> PL = Plenum; L = Lehrende*r; S = Studierende; LV = Lehrvortrag; EA = Einzelarbeit, GA = Gruppenarbeit</p><h3 id="prasenzphase" tabindex="-1">Präsenzphase <a class="header-anchor" href="#prasenzphase" aria-label="Permalink to &quot;Präsenzphase&quot;">​</a></h3><h4 id="einstiegsphase" tabindex="-1">Einstiegsphase <a class="header-anchor" href="#einstiegsphase" aria-label="Permalink to &quot;Einstiegsphase&quot;">​</a></h4><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden / Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 5 min.</td><td>Begrüßung u. Vorstellung der zeitlichen und thematischen Agenda</td><td>L. bietet Orientierung</td><td></td><td>LV</td></tr><tr><td>Ca. 15 min.</td><td>ScaryTale &amp; Diskussion <br> Zum Einstieg wird eine ScaryTale im Plenum „gespielt“. <br> UND <br>Besprechung der zuvor beantworteten Leitfrage sowie des LZ.</td><td>Die S. können kritisch beurteilen(6), auf welche rechtlichen Aspekte im FDM (z. B. bei der Erhebung von Daten) geachtet werden sollte.</td><td>L. führt die ScaryTale ein. Hier sollen die S. im Plenum raten und gemeinsam eine Antwort finden. <br>Daraufhin erfolgt zur Beantwortung des LZ und der Leitfrage, die zuhause im Lernportfolio beantwortet wurden, eine Diskussion.</td><td>PL</td></tr></tbody></table><h4 id="arbeitsphase" tabindex="-1">Arbeitsphase <a class="header-anchor" href="#arbeitsphase" aria-label="Permalink to &quot;Arbeitsphase&quot;">​</a></h4><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden / Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 15 min.</td><td>Einzelarbeit <br> Detaillierte Definition von (inkl. besonders sensiblen) personenbezogenen Daten. <br> Hervorhebung, was bei personenbezogenen Daten im gesamten Forschungsprozess beachtet werden muss. <br> Auflistung von unterschiedlichen Forschungsdaten mit (besonders sensiblen) Personenbezug. Anschließend werden gesammelte Aspekte im PL besprochen.</td><td>Die S. können herausstellen(4), was personenbezogene und sensible Daten sind, und entsprechende Beispiele einordnen(4).</td><td>Die S. können folgende Fragen beantworten (ggf. als eine Art Quiz/Mindmap): <br> Was sind personenbezogene Daten? <br> Welche Aspekte müssen bei der Datenerhebung und –verarbeitung von personenbezogenen Daten beachtet werden? <br> Was sind Beispiele für Forschungsdaten mit (besonders sensiblen) Personenbezug?</td><td>EA / PL</td></tr><tr><td>Ca. 45 min.</td><td>Gruppenarbeit <br> Die S. müssen anhand von Beispielen relevante rechtliche Aspekte hervorheben. <br> In ihren Gruppen, und anschließend im PL, müssen sie Leitfragen beantworten (unten in der Liste notiert).</td><td>Die S. sind in der Lage, die wichtigsten rechtlichen Aspekte im Umgang mit Daten für ein Beispiel einzuordnen(4).</td><td>L. teilt die S. in Gruppen ein. Die Beispiele werden von L. vorgegeben. <br> Die Gruppen müssen dann jeweils zusammenarbeiten, um die Aufgabe zu erledigen. <br> Danach leitet die L. eine offene Diskussion ein und bittet die Gruppen, ihre Arbeit vorzustellen. <br> Die S. sollten folgendes beantworten: <br> Welche Art von Daten sind in diesem Beispiel zu sehen? <br> Wer ist Urheber: in des Datensatzes? <br> Unter welcher Lizenz sind diese Daten veröffentlicht? <br> Dürfen die Daten nachgenutzt werden? <br> Wurden personenbezogene Daten anonymisiert/ pseudonymisiert?</td><td>GA / PL</td></tr></tbody></table><h4 id="abschluss" tabindex="-1">Abschluss <a class="header-anchor" href="#abschluss" aria-label="Permalink to &quot;Abschluss&quot;">​</a></h4><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden / Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 10 min.</td><td>Offene Fragen / Unklarheiten klären Ausblick auf und Arbeitsauftrag für nächste Präsenzveranstaltung</td><td>L. erklärt Erwartungen für nächste Stunde. <br> L. bietet S. an, offene Fragen zum Vorlesungsthema, dem Seminarverlauf und/oder anderen Aspekten zu besprechen.</td><td></td><td>PL</td></tr></tbody></table><h2 id="prasenzphase-1" tabindex="-1">Präsenzphase <a class="header-anchor" href="#prasenzphase-1" aria-label="Permalink to &quot;Präsenzphase&quot;">​</a></h2><h3 id="einstiegsphase-1" tabindex="-1">Einstiegsphase <a class="header-anchor" href="#einstiegsphase-1" aria-label="Permalink to &quot;Einstiegsphase&quot;">​</a></h3><p>Begrüßung</p><p>(ca. 15 Min.) ScaryTale und Diskussion L. trägt das ScaryTale vor und die S. müssen gemeinsam eine Antwort erraten. Kritische Diskussion: Daraufhin beantworten die S. im PL (geleitet von L.) die Leitfrage, die sie vorab im Lernportfolio notiert haben, sowie das LZ dieses Abschnitts.</p><h3 id="arbeitsphase-1" tabindex="-1">Arbeitsphase <a class="header-anchor" href="#arbeitsphase-1" aria-label="Permalink to &quot;Arbeitsphase&quot;">​</a></h3><p>(ca. 15 Min.) Individuelle Bearbeitung der Fragen. L. bereitet die Aufgabe vor (bspw. Quiz oder Mindmap) und erläutert den</p><p>Arbeitsauftrag:</p><p>===</p><p>Die S. bearbeiten die Aufgabe individuell. Anschließend bringt L. die S. zusammen und es gibt eine abschließende Besprechung zu der Aufgabe.</p><p>(ca. 50 Min.) Gruppenarbeit</p><p>===</p><p>L. teilt die S. in Gruppen ein und stellt die Beispiele vor. Die Gruppen setzen sich zusammen und beantworten folgende Fragen für jedes Beispiel (oder ein Beispiel pro Gruppe):</p><ul><li>Welche Art von Daten sind in diesem Beispiel zu sehen?</li><li>Wer ist Urheber*in des Datensatzes?</li><li>Unter welcher Lizenz sind diese Daten veröffentlicht?</li><li>Dürfen die Daten nachgenutzt werden?</li><li>Wurden personenbezogene Daten anonymisiert/pseudonymisiert?</li></ul><p>Nach der Gruppenarbeit bringt L. alle Gruppen zusammen. Jede Gruppe trägt ihre Gedanken mit offener Diskussion vor.</p><h3 id="abschluss-1" tabindex="-1">Abschluss <a class="header-anchor" href="#abschluss-1" aria-label="Permalink to &quot;Abschluss&quot;">​</a></h3><p>L. fragt die S., ob noch Unklarheiten bestehen oder offene Fragen vorhanden sind.</p><p>L. gibt Ausblick auf nächstes Thema und erinnert an die (online) Vorbereitung zur nächsten Präsenzveranstaltung</p><h2 id="taxonomiestufen" tabindex="-1">Taxonomiestufen <a class="header-anchor" href="#taxonomiestufen" aria-label="Permalink to &quot;Taxonomiestufen&quot;">​</a></h2><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">Taxonomiestufe 1</td><td style="text-align:left;">Wissen, Kenntnisse (Tatsachen, Begriffe nennen)</td><td style="text-align:left;">Informationen möglichst wortgenau erinnern wiedergeben können, Informationen sinnerhaltend umformen und in eigenen Worten wiedergeben sowie zusammenfassen können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 2</td><td style="text-align:left;">Verständnis (erklären, begründen, mit anderen Worten umschreiben)</td><td style="text-align:left;"></td></tr><tr><td style="text-align:left;">Taxonomiestufe 3</td><td style="text-align:left;">Anwendung (Anwendung des Wissens, z.B. bei der Lösung eines praktischen Problems)</td><td style="text-align:left;">Abstraktionen (Regeln, Methoden etc.) in konkreten Situationen anwenden können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 4</td><td style="text-align:left;">Analyse (eine reale Situation in ihre Elemente zerlegen)</td><td style="text-align:left;">Ideen, Problemstellungen in ihre Elemente zerlegen und vergleichen können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 5</td><td style="text-align:left;">Synthese (aus verschiedenen Erlebnissen eine neue Situation konstruieren)</td><td style="text-align:left;">Einzelne Elemente zu einer Ganzheit formen</td></tr><tr><td style="text-align:left;">Taxonomiestufe 6</td><td style="text-align:left;">Bewertung (eine Situation beurteilen, überprüfen)</td><td style="text-align:left;">Ein bewertendes Urteil abgeben können</td></tr></tbody></table><h2 id="quellen" tabindex="-1">Quellen: <a class="header-anchor" href="#quellen" aria-label="Permalink to &quot;Quellen:&quot;">​</a></h2><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Lernziele Formulieren leicht gemacht</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/lernziele-formulieren-leicht-gemacht.htm" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/lernziele-formulieren-leicht-gemacht.htm</a></p><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Lernziele definieren</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/lernziele-definieren.html" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/lernziele-definieren.html</a></p><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Formulierungshilfen für Lernziele</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/formulierungshilfen-fur-lernziele.html" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/formulierungshilfen-fur-lernziele.html</a></p>',43),a=[s];function l(d,h,o,u,b,c){return n(),t("div",null,a)}const f=e(r,[["render",l]]);export{m as __pageData,f as default};
