import{_ as e,c as t,o as n,a3 as i}from"./chunks/framework.Dcugq_a2.js";const m=JSON.parse('{"title":"VL11: Ethische Aspekte","description":"","frontmatter":{},"headers":[],"relativePath":"lz/VL_11_Ethik_LZ.md","filePath":"lz/VL_11_Ethik_LZ.md"}'),r={name:"lz/VL_11_Ethik_LZ.md"},a=i('<h1 id="vl11-ethische-aspekte" tabindex="-1">VL11: Ethische Aspekte <a class="header-anchor" href="#vl11-ethische-aspekte" aria-label="Permalink to &quot;VL11: Ethische Aspekte&quot;">​</a></h1><p><strong>Ausgangslage</strong></p><ul><li>Die S. kennen die ethischen Richtlinien in Bezug zum FDM (beim Umgang mit Daten) nicht, weshalb sie ethischen Aspekten nicht bewusst folgen.</li></ul><h2 id="lernziele-fur-den-online-kurs-liascript" tabindex="-1">Lernziele für den Online-Kurs (LiaScript) <a class="header-anchor" href="#lernziele-fur-den-online-kurs-liascript" aria-label="Permalink to &quot;Lernziele für den Online-Kurs (LiaScript)&quot;">​</a></h2><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">1</td><td style="text-align:left;">Die S. können skizzieren (1), auf welche ethischen Aspekte beim Umgang mit Daten geachtet werden muss und können die CARE-Prinzipien beschreiben (1).</td><td style="text-align:left;">Die S. haben die CARE-Prinzipien zur Kenntnis genommen und können diese in der Zukunft beachten / wissen, wie sie weitere Informationen suchen können. Die S. verstehen, auf welche ethischen Aspekte sie im Forschungsprozess achten sollten, auch wenn diese nicht immer vorgeschrieben sind.</td></tr></tbody></table><h3 id="lernziele-fur-den-unterricht-in-prasenz" tabindex="-1">Lernziele für den Unterricht in Präsenz <a class="header-anchor" href="#lernziele-fur-den-unterricht-in-prasenz" aria-label="Permalink to &quot;Lernziele für den Unterricht in Präsenz&quot;">​</a></h3><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">2</td><td style="text-align:left;">Die S. können kritisch beurteilen (6), auf welche ethischen Aspekte im FDM (z. B. bei der Erhebung von Daten) geachtet werden sollte.</td><td style="text-align:left;">Die S. verstehen, auf welche ethischen Aspekte sie im Forschungsprozess (insbesondere bei der Datenerhebung/-veröffentlichung) achten müssen und weshalb das so wichtig ist.</td></tr><tr><td style="text-align:left;">3</td><td style="text-align:left;">Die S. sind in der Lage, die wichtigsten ethischen Aspekte im Umgang mit Daten für ein Beispiel einzuordnen (4).</td><td style="text-align:left;">Die S. können sich beim Umgang mit Daten (in ihrem Studium) auf ethische Richtlinien beziehen (5) und diese beachten. Die S. fühlen sich mit den Konzepten von ethischen Richtlinien sicher, sodass sie sich beim Umgang mit Daten (z. B. für Projektarbeiten / Abschlussarbeiten In ihrem Studium) an diese Richtlinien halten können.</td></tr></tbody></table><h2 id="detaillierte-kursplanung-1" tabindex="-1">Detaillierte Kursplanung[^1] <a class="header-anchor" href="#detaillierte-kursplanung-1" aria-label="Permalink to &quot;Detaillierte Kursplanung[^1]&quot;">​</a></h2><p><strong>Online-Kurs</strong></p><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden / Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 20 min.</td><td>Einführung zum Thema ethische Aspekte im Forschungsprozess. <br>Erläuterung von ethischen Aspekten, die bei der Datenerhebung und -verarbeitung beachtet werden müssen. <br>Unterstreichung eines verantwortlichen Umgangs mit Forschungsdaten.</td><td>Die S. können skizzieren(1), auf welche ethischen Aspekte beim Umgang mit Daten geachtet werden muss</td><td>Die S. müssen die Leitfragen im Lernportfolio beantworten (für eine ggf. Diskussion im Plenum).</td><td>EA</td></tr><tr><td>Ca. 10 min.</td><td>Einführung und Definition der CARE-Prinzipien.</td><td>Die S. können die CARE-Prinzipien beschreiben(1).</td><td>Infotext und Quiz</td><td>EA</td></tr></tbody></table><hr><p>[^1]:<strong>Legende der Sozialformen:</strong> PL = Plenum; L = Lehrende*r; S = Studierende; LV = Lehrvortrag; EA = Einzelarbeit, GA = Gruppenarbeit</p><h3 id="prasenzphase" tabindex="-1">Präsenzphase <a class="header-anchor" href="#prasenzphase" aria-label="Permalink to &quot;Präsenzphase&quot;">​</a></h3><h4 id="einstiegsphase" tabindex="-1">Einstiegsphase <a class="header-anchor" href="#einstiegsphase" aria-label="Permalink to &quot;Einstiegsphase&quot;">​</a></h4><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden / Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 5 min.</td><td>Begrüßung u. Vorstellung der zeitlichen und thematischen Agenda</td><td>L. bietet Orientierung</td><td></td><td>LV</td></tr><tr><td>Ca. 20 min.</td><td>Besprechung der zuvor beantworteten Leitfrage sowie des LZ.</td><td>Die S. können kritisch beurteilen(6), auf welche ethischen Aspekte im FDM (z. B. bei der Erhebung von Daten) geachtet werden sollte.</td><td>Wissenspool <br>Diskussion zur Beantwortung des LZ und der Leitfragen, die zuhause im Lernportfolio beantwortet wurden.</td><td>PL</td></tr></tbody></table><h4 id="arbeitsphase" tabindex="-1">Arbeitsphase <a class="header-anchor" href="#arbeitsphase" aria-label="Permalink to &quot;Arbeitsphase&quot;">​</a></h4><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden / Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 60 min.</td><td>Gruppenarbeit <br>Die S. müssen anhand von Beispielen relevante ethische Aspekte hervorheben. <br>In ihren Gruppen, und anschließend im PL, müssen sie Leitfragen beantworten (unten in der Liste notiert).</td><td>Die S. sind in der Lage, die wichtigsten ethischen Aspekte im Umgang mit Daten für ein Beispiel einzuordnen(4).</td><td>L. teilt die S. in Gruppen ein. Die Beispiele werden von L. vorgegeben. <br>Die Gruppen müssen dann jeweils zusammenarbeiten, um die Aufgabe zu erledigen. <br>Danach leitet die L. eine offene Diskussion ein und bittet die Gruppen, ihre Arbeit vorzustellen. <br>Die S. müssen folgendes beantworten: <br>Wurde bei der Datenerhebung gegen ethische Prinzipien verstoßen? <br>Begründen: Warum oder warum nicht? <br>Würde die Nachnutzung dieser Daten gegen ethische Prinzipien verstoßen? Woran erkennen Sie das?</td><td>GA / PL</td></tr></tbody></table><h4 id="abschluss" tabindex="-1">Abschluss <a class="header-anchor" href="#abschluss" aria-label="Permalink to &quot;Abschluss&quot;">​</a></h4><table><thead><tr><th>Zeit</th><th>Inhalt</th><th>Ziele</th><th>Methoden / Medien (Arbeitsschritte)</th><th>Sozialform</th></tr></thead><tbody><tr><td>Ca. 5. min.</td><td>Offene Fragen / Unklarheiten klären Ausblick auf und Arbeitsauftrag für nächste Präsenzveranstaltung</td><td>L. erklärt Erwartungen für nächste Stunde. <br>L. bietet S. an, offene Fragen zum Vorlesungsthema, dem Seminarverlauf und/oder anderen Aspekten zu besprechen.</td><td></td><td>PL</td></tr></tbody></table><h2 id="prasenzphase-erlauterungen" tabindex="-1">Präsenzphase / Erläuterungen <a class="header-anchor" href="#prasenzphase-erlauterungen" aria-label="Permalink to &quot;Präsenzphase / Erläuterungen&quot;">​</a></h2><h3 id="einstiegsphase-1" tabindex="-1">Einstiegsphase <a class="header-anchor" href="#einstiegsphase-1" aria-label="Permalink to &quot;Einstiegsphase&quot;">​</a></h3><p>Begrüßung</p><h1 id="ca-20-min-wissenspool" tabindex="-1">(ca. 20 Min.) Wissenspool <a class="header-anchor" href="#ca-20-min-wissenspool" aria-label="Permalink to &quot;(ca. 20 Min.)  Wissenspool&quot;">​</a></h1><p>Besprechung der Leitfrage, welche die S. vorab im Lernportfolio notiert haben sowie das LZ dieses Abschnitts.</p><h3 id="arbeitsphase-1" tabindex="-1">Arbeitsphase <a class="header-anchor" href="#arbeitsphase-1" aria-label="Permalink to &quot;Arbeitsphase&quot;">​</a></h3><h1 id="ca-60-min-gruppenarbeit" tabindex="-1">(ca. 60 Min.) Gruppenarbeit <a class="header-anchor" href="#ca-60-min-gruppenarbeit" aria-label="Permalink to &quot;(ca. 60 Min.) Gruppenarbeit&quot;">​</a></h1><p>L. teilt die S. in Gruppen ein und stellt die Beispiele vor. Die Gruppen setzen sich zusammen und beantworten folgende Fragen:</p><ul><li><p>Wurde bei der Datenerhebung gegen ethische Prinzipien verstoßen? Begründen Sie: Warum oder warum nicht?</p></li><li><p>Würde die Nachnutzung dieser Daten gegen ethische Prinzipien verstoßen? Woran erkennen Sie das?</p></li></ul><p>Nach der Gruppenarbeit bringt L. Alle Gruppen zusammen. Jede Gruppe trägt ihre Gedanken mit offener Diskussion vor.</p><h3 id="abschluss-1" tabindex="-1">Abschluss <a class="header-anchor" href="#abschluss-1" aria-label="Permalink to &quot;Abschluss&quot;">​</a></h3><p>L. fragt die S., ob noch Unklarheiten bestehen oder offene Fragen vorhanden sind.</p><p>L. gibt Ausblick auf nächstes Thema und erinnert an die (online) Vorbereitung zur nächsten Präsenzveranstaltung</p><h2 id="taxonomiestufen" tabindex="-1">Taxonomiestufen <a class="header-anchor" href="#taxonomiestufen" aria-label="Permalink to &quot;Taxonomiestufen&quot;">​</a></h2><table><thead><tr><th style="text-align:left;"></th><th style="text-align:left;"></th><th style="text-align:left;"></th></tr></thead><tbody><tr><td style="text-align:left;">Taxonomiestufe 1</td><td style="text-align:left;">Wissen, Kenntnisse (Tatsachen, Begriffe nennen)</td><td style="text-align:left;">Informationen möglichst wortgenau erinnern wiedergeben können, Informationen sinnerhaltend umformen und in eigenen Worten wiedergeben sowie zusammenfassen können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 2</td><td style="text-align:left;">Verständnis (erklären, begründen, mit anderen Worten umschreiben)</td><td style="text-align:left;"></td></tr><tr><td style="text-align:left;">Taxonomiestufe 3</td><td style="text-align:left;">Anwendung (Anwendung des Wissens, z.B. bei der Lösung eines praktischen Problems)</td><td style="text-align:left;">Abstraktionen (Regeln, Methoden etc.) in konkreten Situationen anwenden können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 4</td><td style="text-align:left;">Analyse (eine reale Situation in ihre Elemente zerlegen)</td><td style="text-align:left;">Ideen, Problemstellungen in ihre Elemente zerlegen und vergleichen können</td></tr><tr><td style="text-align:left;">Taxonomiestufe 5</td><td style="text-align:left;">Synthese (aus verschiedenen Erlebnissen eine neue Situation konstruieren)</td><td style="text-align:left;">Einzelne Elemente zu einer Ganzheit formen</td></tr><tr><td style="text-align:left;">Taxonomiestufe 6</td><td style="text-align:left;">Bewertung (eine Situation beurteilen, überprüfen)</td><td style="text-align:left;">Ein bewertendes Urteil abgeben können</td></tr></tbody></table><h2 id="quellen" tabindex="-1">Quellen: <a class="header-anchor" href="#quellen" aria-label="Permalink to &quot;Quellen:&quot;">​</a></h2><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Lernziele Formulieren leicht gemacht</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/lernziele-formulieren-leicht-gemacht.htm" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/lernziele-formulieren-leicht-gemacht.htm</a></p><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Lernziele definieren</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/lernziele-definieren.html" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/lernziele-definieren.html</a></p><p>Meyer Junker, R. &amp; Deutsches Institut für Erwachsenenbildung. (o. D.). <em>Formulierungshilfen für Lernziele</em>. wb-web. Abgerufen am 20. Oktober 2023, von <a href="https://wb-web.de/material/methoden/formulierungshilfen-fur-lernziele.html" target="_blank" rel="noreferrer">https://wb-web.de/material/methoden/formulierungshilfen-fur-lernziele.html</a></p>',38),s=[a];function l(h,d,o,u,b,g){return n(),t("div",null,s)}const f=e(r,[["render",l]]);export{m as __pageData,f as default};
